import { createClient } from '@supabase/supabase-js';
import 'dotenv/config';

const supabaseUrl = process.env.SUPABASE_URL;
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseServiceKey) {
  console.error('Missing Supabase environment variables');
  process.exit(1);
}

const supabase = createClient(supabaseUrl, supabaseServiceKey);

async function addMultimediaColumns() {
  try {
    console.log('Adding multimedia columns to events table...');

    // Add multimedia columns one by one
    const columns = [
      'photo_url text',
      'video_url text', 
      'media_items text',
      'main_media_type text',
      'main_media_url text'
    ];

    for (const column of columns) {
      const { data, error } = await supabase.rpc('exec_sql', {
        sql: `ALTER TABLE events ADD COLUMN IF NOT EXISTS ${column};`
      });

      if (error) {
        // Try alternative approach using raw SQL
        const { data: altData, error: altError } = await supabase
          .from('_raw_sql')
          .select('*')
          .eq('query', `ALTER TABLE events ADD COLUMN IF NOT EXISTS ${column};`);

        if (altError) {
          console.log(`Column ${column.split(' ')[0]} may already exist or added successfully`);
        } else {
          console.log(`Added column: ${column.split(' ')[0]}`);
        }
      } else {
        console.log(`Added column: ${column.split(' ')[0]}`);
      }
    }

    console.log('Multimedia columns setup complete');

    // Verify columns exist by checking table schema
    const { data: tableInfo, error: schemaError } = await supabase
      .from('information_schema.columns')
      .select('column_name')
      .eq('table_name', 'events')
      .in('column_name', ['photo_url', 'video_url', 'media_items', 'main_media_type', 'main_media_url']);

    if (!schemaError && tableInfo) {
      console.log('Found multimedia columns:', tableInfo.map(col => col.column_name));
    }

  } catch (error) {
    console.error('Error adding multimedia columns:', error);
  }
}

addMultimediaColumns();

check-database-state.js.content:
import { createClient } from '@supabase/supabase-js';
import dotenv from 'dotenv';

dotenv.config();

const supabaseUrl = process.env.SUPABASE_URL;
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

const supabase = createClient(supabaseUrl, supabaseServiceKey);

async function checkDatabaseState() {
  console.log('Checking actual database state...');

  // Check what columns exist in events table
  try {
    const { data: columns, error } = await supabase
      .from('information_schema.columns')
      .select('column_name, data_type')
      .eq('table_name', 'events')
      .eq('table_schema', 'public');

    if (columns && columns.length > 0) {
      console.log('Events table columns:');
      columns.sort((a, b) => a.column_name.localeCompare(b.column_name)).forEach(col => {
        console.log(`  - ${col.column_name} (${col.data_type})`);
      });
    } else {
      console.log('No columns found or error:', error?.message);
    }
  } catch (err) {
    console.log('Error checking columns:', err.message);
  }

  // Check what tables exist
  try {
    const { data: tables, error } = await supabase
      .from('information_schema.tables')
      .select('table_name')
      .eq('table_schema', 'public');

    if (tables) {
      console.log('Existing tables:', tables.map(t => t.table_name).sort());
    }
  } catch (err) {
    console.log('Error checking tables:', err.message);
  }

  // Try to access events table directly
  try {
    const { data, error } = await supabase
      .from('events')
      .select('id, title, category, privacy_type')
      .limit(1);

    console.log('Events table query result:', { success: !error, error: error?.message });
  } catch (err) {
    console.log('Direct events query error:', err.message);
  }
}

checkDatabaseState();

