import postgres from 'postgres';
import dotenv from 'dotenv';

dotenv.config();

const createTables = async () => {
  const sql = postgres(process.env.DATABASE_URL);

  try {
    console.log('Creating database tables...');

    // Create users table first
    await sql`
      CREATE TABLE IF NOT EXISTS users (
        id SERIAL PRIMARY KEY,
        username TEXT NOT NULL UNIQUE,
        email TEXT NOT NULL UNIQUE,
        password TEXT,
        name TEXT NOT NULL,
        bio TEXT,
        avatar TEXT,
        supabase_id TEXT UNIQUE,
        stripe_customer_id TEXT,
        stripe_subscription_id TEXT,
        created_at TIMESTAMP DEFAULT NOW() NOT NULL,
        updated_at TIMESTAMP DEFAULT NOW() NOT NULL
      )
    `;

    console.log('Users table created successfully');

    await sql.end();
    return true;
  } catch (error) {
    console.error('Error creating tables:', error);
    await sql.end();
    return false;
  }
};

createTables().then(success => {
  console.log(success ? 'Database setup complete' : 'Database setup failed');
  process.exit(success ? 0 : 1);
});

create-event-attendees-table.js.content:
import { createClient } from '@supabase/supabase-js';
import dotenv from 'dotenv';

dotenv.config();

const supabaseUrl = process.env.SUPABASE_URL;
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseServiceKey) {
  console.error('Missing Supabase credentials');
  process.exit(1);
}

const supabase = createClient(supabaseUrl, supabaseServiceKey);

async function createEventAttendeesTable() {
  try {
    console.log('Creating event_attendees table...');

    // First, create the attendee_status enum if it doesn't exist
    const { error: enumError } = await supabase.rpc('create_enum_if_not_exists', {
      enum_name: 'attendee_status',
      enum_values: ['pending', 'approved', 'rejected']
    });

    if (enumError) {
      console.log('Enum creation result:', enumError.message);
    }

    // Create the table using raw SQL
    const createTableSQL = `
      CREATE TABLE IF NOT EXISTS event_attendees (
        id SERIAL PRIMARY KEY,
        event_id INTEGER NOT NULL REFERENCES events(id),
        user_id INTEGER NOT NULL REFERENCES users(id),
        status attendee_status DEFAULT 'approved' NOT NULL,
        payment_status TEXT DEFAULT 'pending',
        payment_intent_id TEXT,
        created_at TIMESTAMP DEFAULT NOW() NOT NULL,
        UNIQUE(event_id, user_id)
      );
    `;

    const { data, error } = await supabase.rpc('exec_sql', { 
      sql: createTableSQL 
    });

    if (error) {
      console.error('Error creating table:', error);

      // Try alternative approach with direct query
      const { error: directError } = await supabase
        .from('event_attendees')
        .select('*')
        .limit(1);

      if (directError && directError.code === '42P01') {
        console.log('Table does not exist, attempting to create with service key...');
        // We need to create this table through Supabase dashboard or use a different approach
        console.log('Please create the event_attendees table manually in Supabase dashboard with this SQL:');
        console.log(createTableSQL);
      }
    } else {
      console.log('‚úì event_attendees table created successfully');
    }

  } catch (error) {
    console.error('Error:', error);
  }
}

createEventAttendeesTable();

create-notifications-supabase.js.content:
import { createClient } from '@supabase/supabase-js';
import dotenv from 'dotenv';

dotenv.config();

const supabaseUrl = process.env.SUPABASE_URL;
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY || process.env.SUPABASE_ANON_KEY;

if (!supabaseUrl || !supabaseServiceKey) {
  console.error('Missing Supabase credentials');
  process.exit(1);
}

const supabase = createClient(supabaseUrl, supabaseServiceKey);

async function createNotificationsTable() {
  try {
    console.log('üîß Creating notification_type enum...');

    // Create enum type first
    const { error: enumError } = await supabase.rpc('exec_sql', {
      sql: `
        DO $$ BEGIN
          CREATE TYPE notification_type AS ENUM ('new_request', 'request_approved', 'request_rejected');
        EXCEPTION
          WHEN duplicate_object THEN null;
        END $$;
      `
    });

    if (enumError) {
      console.error('Error creating enum:', enumError);
    } else {
      console.log('‚úÖ notification_type enum created/exists');
    }

    console.log('üîß Creating notifications table...');

    // Create notifications table
    const { error: tableError } = await supabase.rpc('exec_sql', {
      sql: `
        CREATE TABLE IF NOT EXISTS notifications (
          id SERIAL PRIMARY KEY,
          user_id INTEGER NOT NULL REFERENCES users(id) ON DELETE CASCADE,
          type notification_type NOT NULL,
          title TEXT NOT NULL,
          message TEXT NOT NULL,
          event_id INTEGER REFERENCES events(id) ON DELETE CASCADE,
          request_id INTEGER,
          is_read BOOLEAN DEFAULT FALSE,
          created_at TIMESTAMPTZ DEFAULT NOW(),
          updated_at TIMESTAMPTZ DEFAULT NOW()
        );

        CREATE INDEX IF NOT EXISTS idx_notifications_user_id ON notifications(user_id);
        CREATE INDEX IF NOT EXISTS idx_notifications_created_at ON notifications(created_at);
        CREATE INDEX IF NOT EXISTS idx_notifications_is_read ON notifications(is_read);
      `
    });

    if (tableError) {
      console.error('Error creating notifications table:', tableError);
    } else {
      console.log('‚úÖ notifications table created successfully');
    }

    // Verify table exists
    const { data: tables, error: verifyError } = await supabase
      .from('information_schema.tables')
      .select('table_name')
      .eq('table_schema', 'public')
      .eq('table_name', 'notifications');

    if (verifyError) {
      console.error('Error verifying table:', verifyError);
    } else if (tables && tables.length > 0) {
      console.log('‚úÖ notifications table verified in database');
    } else {
      console.log('‚ùå notifications table not found after creation');
    }

  } catch (error) {
    console.error('Exception creating notifications table:', error);
  }
}

createNotificationsTable().then(() => {
  console.log('üéâ Notifications table setup complete');
  process.exit(0);
}).catch(error => {
  console.error('Failed to create notifications table:', error);
  process.exit(1);
});

