        console.log('\n' + enumSQL + '\n' + tableSQL);
      } else {
        console.log('‚úì Table appears to exist now');
      }
    } else {
      console.log('‚úì Table created successfully');
    }

  } catch (error) {
    console.error('Unexpected error:', error);
  }
}

createMissingTable();

