import React, { useState, useEffect, useRef } from "react";
import SearchBar from "@/components/search/search-bar";
import GooglePlacesSearch from "@/components/search/google-places-search";
import EventFilters from "@/components/events/event-filters";
import { Circle, MapPin, MapIcon, PinIcon, Plus, Pin, MapPinned, Check, X, Compass, Filter } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import EventPin from "./event-pin";
import { CategoryPin } from "./category-icons";
import mapboxgl, { LngLat } from 'mapbox-gl';
import { initializeMap, getUserLocation, defaultMapConfig, searchLocations, reverseGeocode } from "@/lib/mapbox";
// Para debugging - importar tambi√©n funciones de google
import { reverseGeocode as googleReverseGeocode, findNearbyPlaces } from "@/lib/google-maps";
import { createRoot } from 'react-dom/client';

// Import required types
type Event = {
  id: number;
  title: string;
  description: string;
  category: string;
  date: string;
  latitude: string | number;
  longitude: string | number;
  locationName: string;
  locationAddress: string;
  paymentType: string;
  price?: string | number;
  maxCapacity?: number;
  privacyType: string;
  organizerId: number;
  organizer: {
    id: number;
    name: string;
    avatar?: string;
  };
  attendees: Array<{
    id: number;
    user: {
      id: number;
      name: string;
      avatar?: string;
    };
  }>;
};

type MapViewProps = {
  onEventSelect: (event: Event) => void;
  onCreateEventClick: (locationData?: { 
    latitude: number, 
    longitude: number, 
    locationAddress: string, 
    locationName: string 
  }) => void;
  filters?: {
    category?: string[];
    paymentType?: string[];
    date?: string;
  };
  resetLocationOnFormClose?: boolean;
};



const MapView = ({ onEventSelect, onCreateEventClick, filters, resetLocationOnFormClose }: MapViewProps) => {
  const [filtersVisible, setFiltersVisible] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [locationMode, setLocationMode] = useState(false);
  const [eventsPanelVisible, setEventsPanelVisible] = useState(false);
  const [tempLocationMarker, setTempLocationMarker] = useState<mapboxgl.Marker | null>(null);
  const [tempLocationData, setTempLocationData] = useState<{
    latitude: number;
    longitude: number;
    locationAddress: string;
    locationName: string;
  } | null>(null);

  // Estado para mostrar botones de acci√≥n en ubicaci√≥n actual
  const [showActionsForLocation, setShowActionsForLocation] = useState(false);

  // Estado para guardar las coordenadas y datos de la ubicaci√≥n actual del mapa
  const [currentLocation, setCurrentLocation] = useState<{
    lng: number;
    lat: number;
    locationName?: string;
    locationAddress?: string;
  } | null>(null);

  // Estado para guardar eventos filtrados por una ubicaci√≥n espec√≠fica
  const [locationFilteredEvents, setLocationFilteredEvents] = useState<Event[]>([]);

  const { toast } = useToast();
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const mapRef = useRef<mapboxgl.Map | null>(null);
  const markersRef = useRef<mapboxgl.Marker[]>([]);
  const popupsRef = useRef<mapboxgl.Popup[]>([]);

  // Fetch events from API
  const { data: eventsData, isLoading, error } = useQuery({
    queryKey: ['/api/events'],
    refetchOnWindowFocus: true,
  });

  // Use fetched events or empty array if no data yet
  const events: Event[] = Array.isArray(eventsData) ? eventsData : [];

  // Filter events based on search and filters
  const filteredEvents = events.filter((event: Event) => {
    // Search filter
    if (searchTerm && !event.title.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }

    // Category filter
    if (filters?.category?.length && !filters.category.includes(event.category)) {
      return false;
    }

    // Payment type filter
    if (filters?.paymentType?.length && !filters.paymentType.includes(event.paymentType)) {
      return false;
    }

    // Date filter handling
    if (filters?.date && filters.date !== 'all') {
      const eventDate = new Date(event.date);
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const tomorrow = new Date(today);
      tomorrow.setDate(tomorrow.getDate() + 1);

      const nextWeekStart = new Date(today);
      nextWeekStart.setDate(nextWeekStart.getDate() + 7);

      const nextWeekEnd = new Date(nextWeekStart);
      nextWeekEnd.setDate(nextWeekEnd.getDate() + 7);

      // Get the current day of the week (0 = Sunday, 6 = Saturday)
      const currentDay = today.getDay();

      // Calculate days until next weekend (Friday and Saturday)
      const daysUntilFriday = (5 - currentDay + 7) % 7;
      const daysUntilSunday = (7 - currentDay) % 7;

      // Calculate next weekend dates
      const nextFriday = new Date(today);
      nextFriday.setDate(today.getDate() + daysUntilFriday);

      const nextSunday = new Date(today);
      nextSunday.setDate(today.getDate() + daysUntilSunday);

      switch (filters.date) {
        case 'today':
          // Event date must be today
          return eventDate.toDateString() === today.toDateString();

        case 'tomorrow':
          // Event date must be tomorrow
          return eventDate.toDateString() === tomorrow.toDateString();

        case 'this_week':
          // Event must be within the current week (next 7 days)
          return eventDate >= today && eventDate < nextWeekStart;

        case 'next_week':
          // Event must be within the next week (8-14 days from now)
          return eventDate >= nextWeekStart && eventDate < nextWeekEnd;

        case 'weekend':
          // Event must be on the upcoming weekend (Friday to Sunday)
          return eventDate >= nextFriday && eventDate <= nextSunday;

        default:
          return true;
      }
    }

    return true;
  });

  // Estado para controlar el men√∫ contextual
  const [contextMenu, setContextMenu] = useState<{
    visible: boolean;
    x: number;
    y: number;
    lngLat: mapboxgl.LngLat | null;
  }>({
    visible: false,
    x: 0,
    y: 0,
    lngLat: null
  });

  // Efecto principal para manejar los clics en el mapa (siempre activo)
  useEffect(() => {
    // Esta funci√≥n se ejecutar√° cada vez que el usuario haga clic en el mapa
    function handleMapClick(e: mapboxgl.MapMouseEvent) {
      console.log("Clic en el mapa detectado", locationMode ? "en modo ubicaci√≥n" : "en modo normal");

      // Detener la propagaci√≥n para evitar que otros handlers cierren nuestro men√∫
      e.originalEvent.stopPropagation();

      // Limpiar cualquier marcador temporal previo
      if (tempLocationMarker) {
        tempLocationMarker.remove();
        setTempLocationMarker(null);
      }

      // SIEMPRE mostrar el men√∫ contextual sin importar el modo
      console.log("Abriendo men√∫ contextual en", e.lngLat);

      // IMPORTANTE: Activar el modo de ubicaci√≥n al hacer clic en el mapa
      // Esto permite que el men√∫ contextual se muestre sin tener que hacer clic en "Crear un evento" primero
      if (!locationMode) {
        console.log("Activando modo de ubicaci√≥n autom√°ticamente");
        setLocationMode(true);
      }

      // Crear un nuevo marcador en la ubicaci√≥n del clic
      if (mapRef.current) {
        // Crear el marcador
        const marker = new mapboxgl.Marker({
          color: '#f43f5e', // Color rosa/rojo
          draggable: false
        })
          .setLngLat(e.lngLat)
          .addTo(mapRef.current);

        // Guardar referencia del marcador
        setTempLocationMarker(marker);

        // PASO CRUCIAL: Mostrar el men√∫ contextual INMEDIATAMENTE
        const point = mapRef.current.project(e.lngLat);

        // Actualizar en un solo paso para evitar renders parciales que causen problemas
        setContextMenu({
          visible: true,
          x: point.x,
          y: point.y - 20, // Posicionar por encima del marcador
          lngLat: e.lngLat
        });

        // Tambi√©n guardar los datos de ubicaci√≥n temporales
        setTempLocationData({
          latitude: e.lngLat.lat,
          longitude: e.lngLat.lng,
          locationName: "Ubicaci√≥n seleccionada", 
          locationAddress: "Cargando direcci√≥n..."
        });
      }

      // Usar ambos servicios en paralelo para obtener resultados m√°s completos
      const mapboxPromise = reverseGeocode(e.lngLat.lng, e.lngLat.lat);
      const googlePromise = googleReverseGeocode(e.lngLat.lng, e.lngLat.lat);
      const nearbyPlacesPromise = findNearbyPlaces(e.lngLat.lat, e.lngLat.lng, 100);

      // Procesar todos los resultados
      Promise.all([mapboxPromise, googlePromise, nearbyPlacesPromise])
        .then(([mapboxAddress, googleAddress, nearbyPlaces]) => {
          console.log("Geocode results:", { mapboxAddress, googleAddress });
          console.log("Nearby places:", nearbyPlaces);

          // Extraer nombre del lugar - primero comprobar si hay establecimientos cercanos
          let locationName;
          let address;

          // Prioridad 1: Si encontramos lugares importantes (parques, plazas, etc.) usar esos nombres
          // Identificar posibles lugares importantes (parques, puntos de inter√©s, etc.)
          const importantPlaces = Array.isArray(nearbyPlaces) 
            ? nearbyPlaces.filter(place => 
                // Buscar parques, puntos de inter√©s, atracciones, locales marcados, etc.
                ['park', 'point_of_interest', 'establishment', 'premise', 'neighborhood', 'natural_feature']
                  .some(type => place.types && place.types.includes(type))
              )
            : [];

          if (importantPlaces.length > 0) {
            const place = importantPlaces[0]; // Usar el lugar m√°s relevante
            locationName = place.name || "Lugar del evento";

            // Para la direcci√≥n completa, usar el resultado de geocodificaci√≥n inversa
            if (googleAddress && googleAddress !== "Direcci√≥n no encontrada" && googleAddress !== "Error al obtener direcci√≥n") {
              address = googleAddress;
            } else {
              address = mapboxAddress;
            }

            console.log("Usando nombre de lugar importante:", locationName);
          } 
          // Prioridad 2: Cualquier establecimiento cercano
          else if (Array.isArray(nearbyPlaces) && nearbyPlaces.length > 0) {
            const place = nearbyPlaces[0];
            locationName = place.name || "Lugar del evento";

            // Para la direcci√≥n completa, usar el resultado de geocodificaci√≥n inversa
            if (googleAddress && googleAddress !== "Direcci√≥n no encontrada" && googleAddress !== "Error al obtener direcci√≥n") {
              address = googleAddress;
            } else {
              address = mapboxAddress;
            }

            console.log("Usando nombre de establecimiento cercano:", locationName);
          } 
          // Prioridad 3: Usar los resultados de geocodificaci√≥n inversa de Google
          else if (googleAddress && googleAddress !== "Direcci√≥n no encontrada" && googleAddress !== "Error al obtener direcci√≥n") {
            // Buscar un nombre significativo que no sea solo una direcci√≥n
            // Primero verificar si hay algo como "Parque X" o "Plaza Y" en la direcci√≥n
            const specialPlaceMatch = googleAddress.match(/(Parque|Plaza|Museo|Estadio|Monumento|Jard√≠n|Biblioteca|Universidad|Teatro|Centro)\s+([^,]+)/i);

            if (specialPlaceMatch) {
              // Usamos el nombre del lugar especial si lo encontramos
              locationName = specialPlaceMatch[0];
            } else {
              // Si no, usamos la primera parte de la direcci√≥n
              const placeNameMatch = googleAddress.match(/^([^,]+)/);
              locationName = placeNameMatch ? placeNameMatch[0] : "Lugar del evento";
            }

            address = googleAddress;
            console.log("Usando direcci√≥n de Google:", address);
          } 
          // √öltima opci√≥n: MapBox como fallback
          else {
            // Buscar un nombre significativo que no sea solo una direcci√≥n
            const specialPlaceMatch = mapboxAddress.match(/(Parque|Plaza|Museo|Estadio|Monumento|Jard√≠n|Biblioteca|Universidad|Teatro|Centro)\s+([^,]+)/i);

            if (specialPlaceMatch) {
              // Usamos el nombre del lugar especial si lo encontramos
              locationName = specialPlaceMatch[0];
            } else {
              // Si no, usamos la primera parte de la direcci√≥n
              const placeNameMatch = mapboxAddress.match(/^([^,]+)/);
              locationName = placeNameMatch ? placeNameMatch[0] : "Lugar del evento";
            }

            address = mapboxAddress;
            console.log("Usando direcci√≥n de MapBox (fallback):", address);
          }

          // Store location data
          const locationData = {
            latitude: e.lngLat.lat,
            longitude: e.lngLat.lng,
            locationAddress: address,
            locationName: locationName
          };

          console.log("Guardando datos de ubicaci√≥n:", locationData);
          setTempLocationData(locationData);

          toast({
            title: "Ubicaci√≥n seleccionada",
            description: `${locationName}`,
          });
        })
        .catch(error => {
          console.error("Error getting address or nearby places:", error);
          toast({
            title: "Error",
            description: "No se pudo obtener la direcci√≥n. Por favor, intenta de nuevo.",
            variant: "destructive",
          });
        });
    }

    if (mapRef.current) {
      console.log("Configurando manejador de clics en el mapa");

      // Primero eliminar cualquier manejador existente para evitar duplicados
      mapRef.current.off('click', handleMapClick);

      // Luego a√±adir el manejador de clics
      mapRef.current.on('click', handleMapClick);

      // Return cleanup function
      return () => {
        if (mapRef.current) {
          console.log("Eliminando manejador de clics del mapa");
          mapRef.current.off('click', handleMapClick);
        }
      };
    }
  }, [toast, tempLocationMarker]); // Removed locationMode from dependencies to ensure the click handler stays consistent

  // Initialize map on component mount and center on user's location
  useEffect(() => {
    if (!mapContainerRef.current || mapRef.current) return;

    console.log("Inicializando mapa y configurando eventos globales");

    // Initialize the map with default config
    mapRef.current = initializeMap(mapContainerRef.current);

    // Add controls
    mapRef.current.addControl(new mapboxgl.NavigationControl(), 'top-right');

    // Try to center on user's location automatically
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { longitude, latitude } = position.coords;

        if (mapRef.current) {
          mapRef.current.setCenter([longitude, latitude]);
          mapRef.current.setZoom(13);

          // Add a marker for the user's current position
          new mapboxgl.Marker({ color: '#1DA1F2' })
            .setLngLat([longitude, latitude])
            .addTo(mapRef.current);

          // Try to get the city name for a better user experience
          reverseGeocode(longitude, latitude)
            .then(address => {
              toast({
                title: "Ubicaci√≥n detectada",
                description: `Te mostramos eventos cercanos a tu ubicaci√≥n`,
              });
            })
            .catch(err => console.error("Error getting address:", err));
        }
      },
      (error) => {
        console.warn("Error getting location:", error);
        // Fallback to default location (centered on a major city)
        if (mapRef.current) {
          // Center on Buenos Aires by default (more relevant for Spanish-speaking users)
          mapRef.current.setCenter([-58.3816, -34.6037]);
          mapRef.current.setZoom(12);
        }
      },
      { enableHighAccuracy: true, timeout: 5000, maximumAge: 0 }
    );

    return () => {
      console.log("Limpiando el mapa al desmontar componente");

      // Clean up on unmount
      if (mapRef.current) {
        try {
          // Limpiar los marcadores y popups primero
          markersRef.current.forEach(marker => marker.remove());
          popupsRef.current.forEach(popup => popup.remove());
          markersRef.current = [];
          popupsRef.current = [];

          // Eliminar cualquier marcador temporal
          if (tempLocationMarker) {
            tempLocationMarker.remove();
            setTempLocationMarker(null);
          }

          // Eliminar el mapa
          mapRef.current.remove();
          mapRef.current = null;
        } catch (error) {
          console.error("Error limpiando recursos del mapa:", error);
        }
      }
    };
  }, []);

  // Efecto para cerrar el men√∫ contextual y limpiar marcadores temporales al hacer clic en cualquier parte de la aplicaci√≥n 
  // que no sea el mapa o los botones de acci√≥n
  useEffect(() => {
    function handleClickOutside(e: MouseEvent) {
      const target = e.target as Element;

      // Comprobar si es un clic dentro del mapa
      const isMapClick = target.closest('.mapboxgl-canvas-container');

      // Comprobar si es un clic en los botones de acci√≥n
      const isActionButton = target.closest('button') && (
        target.textContent?.includes('Buscar eventos aqu√≠') || 
        target.textContent?.includes('Crear un evento aqu√≠') ||
        target.textContent?.includes('Ver eventos cercanos') ||
        target.textContent?.includes('Crear evento aqu√≠')
      );

      // Comprobar si es un clic en el men√∫ contextual
      const isContextMenuClick = target.closest('.context-menu');

      // IMPORTANTE: Evitamos cerrar el men√∫ contextual con clicks outside
      // Solo cerramos en casos muy espec√≠ficos, por ejemplo si se hace clic en otra parte 
      // de la aplicaci√≥n que no sea el mapa, el men√∫ o los botones de acci√≥n
      // Agregamos un log para depuraci√≥n
      if (contextMenu.visible) {
        console.log("Click outside detectado - contextMenu visible, analizando objetivo:", {
          isMapClick,
          isContextMenuClick,
          isActionButton
        });

        // Solo cerramos si no es ninguno de los elementos permitidos
        if (!isMapClick && !isContextMenuClick && !isActionButton) {
          console.log("Cerrando men√∫ contextual por clic fuera de elementos permitidos");
          closeContextMenu();
        } else {
          console.log("Manteniendo men√∫ contextual visible");
        }
      }

      // Limpiar el marcador temporal y ocultar botones de acci√≥n si el clic no fue en:
      // - El mapa
      // - Los botones de acci√≥n espec√≠ficos
      // - La barra de b√∫squeda
      // - Controles del mapa
      if (showActionsForLocation && 
          !isMapClick && 
          !isActionButton && 
          !target.closest('.google-places-search') && 
          !target.closest('.mapboxgl-ctrl')) {

        console.log("Clic fuera de la ubicaci√≥n seleccionada, ocultando botones de acci√≥n");

        // Ocultar botones de acciones espec√≠ficas
        setShowActionsForLocation(false);

        // Limpiar el marcador temporal si existe
        if (tempLocationMarker) {
          tempLocationMarker.remove();
          setTempLocationMarker(null);
        }
      }
    }

    document.addEventListener('click', handleClickOutside);

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [contextMenu.visible, showActionsForLocation, tempLocationMarker]);

  // Efecto para detectar cuando se cierra el formulario de creaci√≥n de evento
  useEffect(() => {
    if (resetLocationOnFormClose) {
      console.log("Detectada se√±al de cierre de formulario, limpiando marcadores temporales");
      cleanupTempMarkers();
    }
  }, [resetLocationOnFormClose]);

  // Add event markers when events or filters change
  useEffect(() => {
    if (!mapRef.current) return;

    // Clear existing markers and popups
    markersRef.current.forEach(marker => marker.remove());
    popupsRef.current.forEach(popup => popup.remove());
    markersRef.current = [];
    popupsRef.current = [];

    // Add markers for filtered events
    filteredEvents.forEach((event: Event) => {
      if (!mapRef.current) return;

      // Create custom React element for the marker
      const el = document.createElement('div');
      el.className = "custom-marker-container";

      // Render React component to the element
      const root = createRoot(el);
      root.render(<EventPin category={event.category} />);

      // Create popup
      const popup = new mapboxgl.Popup({ offset: 25 })
        .setHTML(`
          <div class="popup-content">
            <h3 class="font-bold">${event.title}</h3>
            <p class="text-sm">${event.locationName}</p>
            <p class="text-xs text-gray-500">
              ${new Date(event.date).toLocaleDateString('es-ES', { 
                month: 'short', 
                day: 'numeric',
                hour: '2-digit',
                minute: '2-digit'
              })}
            </p>
          </div>
        `);

      // Validar y convertir coordenadas
      const lng = typeof event.longitude === 'string' ? parseFloat(event.longitude) : event.longitude;
      const lat = typeof event.latitude === 'string' ? parseFloat(event.latitude) : event.latitude;

      // Verificar que las coordenadas sean n√∫meros v√°lidos
      if (isNaN(lng) || isNaN(lat)) {
        console.error('Coordenadas inv√°lidas para el evento:', event.id, event.title, {
          longitude: event.longitude,
          latitude: event.latitude
        });
        return; // Saltar este evento si las coordenadas no son v√°lidas
      }

      // Crear marcador solo si las coordenadas son v√°lidas
      const marker = new mapboxgl.Marker(el)
        .setLngLat([lng, lat])
        .setPopup(popup)
        .addTo(mapRef.current);

      // Add event listener to marker
      el.addEventListener('click', () => {
        onEventSelect(event);
      });

      // Track markers and popups for cleanup
      markersRef.current.push(marker);
      popupsRef.current.push(popup);
    });
  }, [filteredEvents, onEventSelect]);

  // Handle search functionality
  const handleSearch = (term: string) => {
    setSearchTerm(term);
  };

  // Funci√≥n para limpiar los marcadores temporales y resetear el estado relacionado
  const cleanupTempMarkers = () => {
    console.log("Ejecutando limpieza forzada de marcadores temporales");

    // Limpiar el marcador temporal si existe
    if (tempLocationMarker) {
      console.log("Eliminando marcador temporal existente");
      tempLocationMarker.remove();
      setTempLocationMarker(null);
    }

    // Tambi√©n limpiar los datos de ubicaci√≥n temporal
    setTempLocationData(null);

    // Resetear el modo de ubicaci√≥n si est√° activo
    if (locationMode) {
      console.log("Desactivando modo de ubicaci√≥n");
      setLocationMode(false);
    }

    // Ocultar los botones de acci√≥n
    setShowActionsForLocation(false);

    // Cerrar cualquier men√∫ contextual abierto
    setContextMenu({
      visible: false,
      x: 0,
      y: 0,
      lngLat: null
    });

    // Forzar limpieza de cualquier otro marcador que podr√≠a haberse quedado
    // Este enfoque es m√°s agresivo pero asegura que no queden marcadores hu√©rfanos
    if (mapRef.current) {
      // Obtener todos los marcadores del mapa
      const markers = Array.from(document.querySelectorAll('.mapboxgl-marker'));
      if (markers.length > 0) {
        console.log(`Limpiando ${markers.length} marcadores encontrados en el DOM`);

        // Eliminar solo los marcadores temporales (para evitar eliminar los de eventos)
        markers.forEach(markerElement => {
          // Los marcadores de eventos tienen la clase custom-marker-container como hijo
          const isEventMarker = markerElement.querySelector('.custom-marker-container');
          if (!isEventMarker) {
            console.log("Eliminando marcador hu√©rfano");
            markerElement.remove();
          }
        });
      }
    }
  };

  // Funci√≥n para cerrar el men√∫ contextual 
  const closeContextMenu = () => {
    // Asegurarse de que el men√∫ contextual se cierre correctamente
    setContextMenu({
      visible: false,
      x: 0,
      y: 0,
      lngLat: null
    });

    // Tambi√©n ocultar los botones de acci√≥n que podr√≠an estar visibles
    setShowActionsForLocation(false);

    // Siempre limpiar el marcador temporal para que no quede visible en el mapa
    // Esto asegura que al hacer clic en Cancelar, siempre se elimine el marcador
    if (tempLocationMarker) {
      console.log("Eliminando marcador temporal al cerrar/cancelar acci√≥n");
      tempLocationMarker.remove();
      setTempLocationMarker(null);
