          const newMediaItems = typeof updateData.mediaItems === 'string' 
            ? JSON.parse(updateData.mediaItems) 
            : updateData.mediaItems;

          if (JSON.stringify(originalMediaItems) !== JSON.stringify(newMediaItems)) {
            mediaChanged = true;
            console.log("Cambio detectado en colecci√≥n de medios");
          }
        } catch (error) {
          console.error("Error comparando mediaItems:", error);
        }
      }

      // Update event
      const updatedEvent = await storage.updateEvent(eventId, updateData);
      console.log("Evento actualizado correctamente.");
      console.log(`¬øHubo cambios en los medios? ${mediaChanged ? 'S√ç' : 'NO'}`);

      // Broadcast event update to all connected clients via WebSocket
      broadcastMessage({
        type: 'event_updated',
        event: updatedEvent,
        updatedBy: user.id  // Agregar el ID del usuario que realiz√≥ la actualizaci√≥n
      });

      // Si se actualiz√≥ el medio principal, env√≠a un mensaje espec√≠fico para esto
      if (mediaChanged) {
        broadcastMessage({
          type: 'event_media_updated',
          eventId: updatedEvent.id,
          updatedBy: user.id
        });
      }

      console.log("Notificaci√≥n de actualizaci√≥n de evento enviada a todos los clientes WebSocket");

      res.json(updatedEvent);
    } catch (error) {
      console.error("Error updating event:", error);
      res.status(500).json({ message: "Error updating event" });
    }
  });

