      console.log("Evento actualizado correctamente.");
      console.log(`¬øHubo cambios en los medios? ${mediaChanged ? 'S√ç' : 'NO'}`);

      // Broadcast event update to all connected clients via WebSocket
      broadcastMessage({
        type: 'event_updated',
        event: updatedEvent,
        updatedBy: user.id  // Agregar el ID del usuario que realiz√≥ la actualizaci√≥n
      });

      // Si se actualiz√≥ el medio principal, env√≠a un mensaje espec√≠fico para esto
      if (mediaChanged) {
