                if (item.url && !shouldRemove) {
                  // MEJORA: Mantener el flag isMain exactamente como viene del cliente
                  // sin normalizar a booleano en este punto para preservar mejor la selecci√≥n
                  const mediaItem = {
                    type: item.type || 'photo',
