import React, { createContext, useContext, useRef, useCallback, ReactNode } from 'react';
import mapboxgl from 'mapbox-gl';

interface CameraState {
  center: mapboxgl.LngLat;
  zoom: number;
  bearing: number;
  pitch: number;
}

interface MapContextType {
  mapRef: React.MutableRefObject<mapboxgl.Map | null>;
  savedCameraState: React.MutableRefObject<CameraState | null>;
  saveCameraState: () => void;
  restoreCameraState: () => void;
  setMapInstance: (map: mapboxgl.Map) => void;
  clearMapInstance: () => void;
}

const MapContext = createContext<MapContextType | undefined>(undefined);

export const MapProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const mapRef = useRef<mapboxgl.Map | null>(null);
  const savedCameraState = useRef<CameraState | null>(null);

  const setMapInstance = useCallback((map: mapboxgl.Map) => {
    mapRef.current = map;
    console.log("üó∫Ô∏è MAP: Instancia del mapa configurada en el contexto");
    console.log("üó∫Ô∏è MAP: mapRef.current ahora es:", mapRef.current);
  }, []);

  // Limpiar referencias cuando el componente se desmonte
  const clearMapInstance = useCallback(() => {
    mapRef.current = null;
    savedCameraState.current = null;
    console.log("üó∫Ô∏è MAP: Referencias del mapa limpiadas del contexto");
  }, []);

  const saveCameraState = useCallback(() => {
    console.log("üö® DEBUG: saveCameraState() EJECUT√ÅNDOSE");
    console.log("üö® DEBUG: mapRef.current =", mapRef.current);
    if (!mapRef.current) {
      console.warn("‚ö†Ô∏è CAMERA: No hay instancia del mapa para guardar el estado");
      return;
    }

    const currentState: CameraState = {
      center: mapRef.current.getCenter(),
      zoom: mapRef.current.getZoom(),
      bearing: mapRef.current.getBearing(),
      pitch: mapRef.current.getPitch()
    };

    savedCameraState.current = currentState;
    console.log("üé• CAMERA: Estado de c√°mara guardado:", currentState);
  }, []);

  const restoreCameraState = useCallback(() => {
    console.log("üö® DEBUG: restoreCameraState() EJECUT√ÅNDOSE");
    console.log("üö® DEBUG: mapRef.current =", mapRef.current);
    console.log("üö® DEBUG: savedCameraState.current =", savedCameraState.current);
    console.log("üîÑ RESTORE: Intentando restaurar c√°mara...");

    if (!mapRef.current) {
      console.error("‚ùå RESTORE: No hay instancia del mapa");
      return;
    }

    if (!savedCameraState.current) {
      console.warn("‚ö†Ô∏è RESTORE: No hay estado guardado de la c√°mara");
      return;
    }

    console.log("üîÑ RESTORE: Ejecutando flyTo con estado:", savedCameraState.current);

    mapRef.current.flyTo({
      center: savedCameraState.current.center,
      zoom: savedCameraState.current.zoom,
      bearing: savedCameraState.current.bearing,
      pitch: savedCameraState.current.pitch,
      duration: 1200,
      essential: true
    });

    console.log("üîÑ RESTORE: flyTo ejecutado exitosamente");

    // Limpiar el estado guardado despu√©s de la restauraci√≥n
    savedCameraState.current = null;
    console.log("üîÑ RESTORE: Estado guardado limpiado");
  }, []);

  const value: MapContextType = {
    mapRef,
    savedCameraState,
    saveCameraState,
    restoreCameraState,
    setMapInstance,
    clearMapInstance
  };

  return (
    <MapContext.Provider value={value}>
      {children}
    </MapContext.Provider>
  );
};

export const useMap = (): MapContextType => {
  const context = useContext(MapContext);
  if (context === undefined) {
    throw new Error('useMap debe ser usado dentro de un MapProvider');
  }
  return context;
};

