// Google Maps utils
import { useLoadScript, Libraries } from '@react-google-maps/api';

// Using environment variable for Google Maps API key
export const GOOGLE_MAPS_API_KEY = import.meta.env.VITE_GOOGLE_MAPS_API_KEY;

// Libraries necesarias
export const libraries: Libraries = ["places"];

// Hook para cargar Google Maps 
export function useGoogleMaps() {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: GOOGLE_MAPS_API_KEY,
    libraries,
  });

  return { isLoaded, loadError };
}

// Configuraci√≥n por defecto del mapa
export const defaultMapConfig = {
  center: {
    lat: 19.4326, // Ciudad de M√©xico por defecto
    lng: -99.1332
  },
  zoom: 11,
  mapContainerStyle: {
    width: '100%',
    height: '100%',
  },
  options: {
    disableDefaultUI: false,
    zoomControl: true,
    mapTypeControl: false,
    streetViewControl: false,
    fullscreenControl: true,
  },
};

// Funci√≥n para geocodificar en reversa (obtener direcci√≥n a partir de coordenadas) usando proxy
// Versi√≥n optimizada para mayor velocidad
export async function reverseGeocode(lng: number, lat: number): Promise<string> {
  try {
    // Validar que las coordenadas sean n√∫meros v√°lidos
    if (isNaN(lat) || isNaN(lng) || !isFinite(lat) || !isFinite(lng)) {
      console.error("Coordenadas inv√°lidas para geocodificaci√≥n:", lat, lng);
      return "Coordenadas inv√°lidas";
    }

    // Usar el proxy del servidor para evitar problemas de CORS
    // Limitamos los resultados a 1 para mayor velocidad
    const response = await fetch(
      `/api/google-proxy/geocode/json?latlng=${lat},${lng}&language=es&region=ar&result_type=street_address|route|premise|point_of_interest&location_type=ROOFTOP|GEOMETRIC_CENTER`
    );

    if (!response.ok) {
      console.error("Error en respuesta HTTP de geocodificaci√≥n inversa:", response.status);
      throw new Error(`Error HTTP ${response.status}`);
    }

    const data = await response.json();

    if (data.status === 'OK' && data.results && data.results.length > 0) {
      const result = data.results[0];
      return result.formatted_address || "Direcci√≥n no encontrada";
    }

    return "Direcci√≥n no encontrada";
  } catch (error) {
    console.error("Error en geocodificaci√≥n inversa:", error);
    return "Error al obtener direcci√≥n";
  }
}

// Funci√≥n para buscar establecimientos cercanos a una ubicaci√≥n (versi√≥n optimizada)
export async function findNearbyPlaces(lat: number, lng: number, radius: number = 100): Promise<any[]> {
  try {
    // Validar que las coordenadas sean n√∫meros v√°lidos
    if (isNaN(lat) || isNaN(lng) || !isFinite(lat) || !isFinite(lng)) {
      console.error("Coordenadas inv√°lidas para b√∫squeda de lugares cercanos:", lat, lng);
      return [];
    }

    // Construir URL m√°s optimizada, limitando el n√∫mero de resultados y filtrando por tipos relevantes
    const url = `/api/google-proxy/place/nearbysearch/json?location=${lat},${lng}&radius=${radius}&rankby=prominence&key=${GOOGLE_MAPS_API_KEY}`;

    // Usar AbortController para limitar tiempo de espera
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 2000); // 2 segundos m√°ximo

    try {
      const response = await fetch(url, { signal: controller.signal });

      clearTimeout(timeoutId); // Limpiar timeout si la respuesta fue exitosa

      if (!response.ok) {
        console.error("Error en la respuesta del servidor para lugares cercanos:", response.status);
        return [];
      }

      const data = await response.json();

      if (data.status === 'OK' && data.results) {
        return data.results.slice(0, 5); // Limitar a 5 resultados para mayor velocidad
      }

      return [];
    } catch (fetchError: any) {
      if (fetchError?.name === 'AbortError') {
        console.log('B√∫squeda de lugares cercanos cancelada por timeout');
      } else {
        console.error("Error en b√∫squeda de lugares cercanos:", fetchError);
      }
      return [];
    }
  } catch (error) {
    console.error("Error en b√∫squeda de lugares cercanos:", error);
    return [];
  }
}

// Funci√≥n para buscar lugares
export async function searchPlaces(query: string): Promise<any[]> {
  try {
    // Intentar obtener la ubicaci√≥n actual para mejorar la b√∫squeda
    let locationParam = '';

    try {
      const position = await new Promise<GeolocationPosition>((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0
        });
      });

      if (position && position.coords) {
        locationParam = `&location=${position.coords.latitude},${position.coords.longitude}&radius=50000`;
        console.log('Usando ubicaci√≥n actual para mejorar resultados de b√∫squeda');
      }
    } catch (locError) {
      console.log('No se pudo obtener la ubicaci√≥n actual para la b√∫squeda:', locError);
    }

    // Construir URL con par√°metros adicionales para mejorar resultados
    const url = `/api/google-proxy/place/textsearch/json?query=${encodeURIComponent(query)}${locationParam}&key=${GOOGLE_MAPS_API_KEY}`;
    console.log('Realizando b√∫squeda en Google Places:', url);

    const response = await fetch(url);

    if (!response.ok) {
      console.error("Error en la respuesta del servidor:", response.status);
      return [];
    }

    const data = await response.json();
    console.log('Respuesta de Google Places:', data.status, data.results?.length || 0, 'resultados');

    if (data.status === 'OK' && data.results) {
      // Mapear resultados al formato requerido por la aplicaci√≥n
      return data.results.map((place: any) => ({
        id: place.place_id,
        place_name: place.formatted_address,
        text: place.name,
        center: [place.geometry.location.lng, place.geometry.location.lat],
        properties: {
          category: place.types?.[0] || 'place'
        }
      }));
    }

    if (data.status !== 'OK') {
      console.error('Error en la respuesta de Google Places:', data.status, data.error_message || 'Sin mensaje de error');
    }

    return [];
  } catch (error) {
    console.error("Error en b√∫squeda de lugares:", error);
    return [];
  }
}

