import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { apiRequest } from "@/lib/queryClient";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Link } from "wouter";

export default function Dashboard() {
  const [location, setLocation] = useLocation();
  const { toast } = useToast();

  // Check if user is authenticated
  const { data: user, isLoading: isUserLoading, error: userError } = useQuery({
    queryKey: ['/api/auth/me'],
    retry: false,
    refetchOnWindowFocus: false
  });

  // Fetch user's created events
  const { data: createdEvents, isLoading: isCreatedEventsLoading } = useQuery({
    queryKey: ['/api/events/created'],
    enabled: !!user,
    retry: false
  });

  // Fetch user's attending events
  const { data: attendingEvents, isLoading: isAttendingEventsLoading } = useQuery({
    queryKey: ['/api/events/attending'],
    enabled: !!user,
    retry: false
  });

  const handleLogout = async () => {
    try {
      const response = await apiRequest("POST", "/api/auth/logout", {});

      if (response.ok) {
        toast({
          title: "Logged out successfully",
        });
        setLocation("/");
      } else {
        throw new Error("Logout failed");
      }
    } catch (error) {
      toast({
        title: "Logout failed",
        description: "An error occurred while logging out",
        variant: "destructive",
      });
    }
  };

  // If not authenticated, redirect to login
  if (userError) {
    setLocation("/login");
    return null;
  }

  // Show loading state
  if (isUserLoading) {
    return (
      <div className="container mx-auto py-10 px-4 text-center">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto py-10 px-4">
