                                // Procesar los elementos multimedia para compatibilidad con el c√≥digo existente
                                const mainItem = safeMediaItems.find(item => item && item.isMain === true);

                                console.log("=== PROCESANDO onChange EN MEDIA MANAGER ===");
                                console.log("¬øHay elemento principal expl√≠cito?", !!mainItem);

                                if (mainItem) {
