    if (organizerId !== parseInt(String(user.id))) {
      console.log(`Usuario ${user.id} intent√≥ editar evento del organizador ${organizerId}`);
      toast({
        title: 'No Autorizado',
        description: 'Solo el organizador puede editar este evento',
        variant: 'destructive',
      });
