                // Filter out invalid items and ensure proper structure
                mediaItemsArray = mediaItemsArray
                  .filter((item: any) => {
                    return (
                      item &&
                      item.url &&
                      item.type &&
                      (item.type === "photo" || item.type === "video")
                    );
