      <div className="mb-4">
        <Button
          className="w-full py-3 bg-white/20 text-white rounded-xl font-medium border border-white/30 hover:bg-white/30 transition-all duration-200"
          onClick={handleApplyFilters}
        >
          Aplicar Filtros
        </Button>
      </div>
      </motion.div>
    </AnimatePresence>
  );
};

export default EventFilters;

