import React, { useState, useEffect, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Trash, Video, Image, Star, StarOff, GripVertical, AlertCircle, Loader2 } from 'lucide-react';
import { cn } from '@/lib/utils';
import { useToast } from '@/hooks/use-toast';
import MediaPreview from './media-preview';

export type MediaItem = {
  id?: string;
  type: 'photo' | 'video';
  url?: string; // URL para medios existentes
  file?: File; // File para nuevos medios
  previewUrl?: string; // URL temporal para vista previa (solo para UI)
  isMain?: boolean;
  isNew?: boolean;
  deleted?: boolean; // Marca para vista (ocultar de la UI)
  toDelete?: boolean; // Marca para enviar al servidor (eliminaci√≥n permanente)
  order?: number; // Para ordenamiento
  uploading?: boolean; // Estado de carga
  uploadProgress?: number; // Progreso de carga (0-100)
  errorMessage?: string; // Mensaje de error si falla la carga
  fileIndex?: number; // √çndice del archivo para seguimiento en el servidor
};

type MediaManagerProps = {
  existingMedia?: MediaItem[] | string; // Puede ser string para JSON o MediaItem[]
  onChange: (media: MediaItem[]) => void;
  maxPhotos?: number;
  maxVideos?: number;
  disabled?: boolean;
};

export const MediaManager: React.FC<MediaManagerProps> = ({
  existingMedia = [],
  onChange,
  maxPhotos = 6,
  maxVideos = 3,
  disabled = false,
}) => {
  const { toast } = useToast();
  // Inicializaci√≥n segura de mediaItems
  const [mediaItems, setMediaItems] = useState<MediaItem[]>(() => {
    // Manejar caso donde existingMedia es undefined o null
    if (existingMedia === undefined || existingMedia === null) {
      console.log("existingMedia es undefined/null, inicializando como array vac√≠o");
      return [];
    }

    // Manejar caso donde existingMedia es string
    if (typeof existingMedia === 'string') {
      try {
        // Si es string vac√≠o, devolver array vac√≠o
        if (!existingMedia.trim()) {
          return [];
        }

        const parsed = JSON.parse(existingMedia);

        // Verificar que el resultado sea un array
        if (!Array.isArray(parsed)) {
          console.warn("existingMedia parseado no es un array:", parsed);
          return [];
        }

        // Filtrar elementos inv√°lidos
        return parsed.filter(item => item !== null && item !== undefined);
      } catch (e) {
        console.error('Error parsing media JSON:', e);
        return [];
      }
    }

    // Si es array, asegurarse de que todos los elementos son v√°lidos
    if (Array.isArray(existingMedia)) {
      return existingMedia.filter(item => item !== null && item !== undefined);
    }

    // Caso por defecto
    console.warn("existingMedia no es un tipo reconocido:", typeof existingMedia);
    return [];
  });

  const [photoCount, setPhotoCount] = useState(0);
  const [videoCount, setVideoCount] = useState(0);
  const [draggedItem, setDraggedItem] = useState<number | null>(null);
  const [dragOverItem, setDragOverItem] = useState<number | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Actualizar los contadores cuando cambia la lista de medios
  useEffect(() => {
    if (!Array.isArray(mediaItems)) {
      console.error("mediaItems no es un array en useEffect");
      return;
    }

    // Contar fotos y videos v√°lidos (no eliminados o marcados para eliminar)
    const photos = mediaItems.filter(
      item => item && item.type === 'photo' && !item.deleted && !item.toDelete
    );
    const videos = mediaItems.filter(
      item => item && item.type === 'video' && !item.deleted && !item.toDelete
    );

    setPhotoCount(photos.length);
    setVideoCount(videos.length);
  }, [mediaItems]);

  // Abrir el selector de archivos
  const handleAddMedia = (isPhoto: boolean) => {
    if (disabled) return;

    // Verificar si hay espacio disponible
    if (isPhoto && photoCount >= maxPhotos) {
      toast({
        title: "L√≠mite alcanzado",
        description: `No puedes agregar m√°s de ${maxPhotos} fotos`,
        variant: "destructive"
      });
      return;
    }

    if (!isPhoto && videoCount >= maxVideos) {
      toast({
        title: "L√≠mite alcanzado",
        description: `No puedes agregar m√°s de ${maxVideos} videos`,
        variant: "destructive"
      });
      return;
    }

    // Verificar y crear el input de archivos si es necesario
    if (!fileInputRef.current) {
      console.error("Input de archivos no encontrado");
      return;
    }

    // Establecer los tipos de archivos aceptados
    fileInputRef.current.accept = isPhoto ? 'image/*' : 'video/*';
    fileInputRef.current.dataset.type = isPhoto ? 'photo' : 'video';

    // Abrir el selector de archivos
    fileInputRef.current.click();
  };

  // Manejar la selecci√≥n de archivos
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (disabled) return;

    const files = Array.from(e.target.files || []);
    if (files.length === 0) return;

    const isPhoto = e.target.dataset.type === 'photo';
    console.log(`Agregando ${files.length} ${isPhoto ? 'fotos' : 'videos'}`);

    // Calcular espacio disponible
    const currentCount = isPhoto ? photoCount : videoCount;
    const maxAllowed = isPhoto ? maxPhotos : maxVideos;
    const availableSlots = Math.max(0, maxAllowed - currentCount);

    if (availableSlots === 0) {
      toast({
        title: "L√≠mite alcanzado",
        description: `No puedes agregar m√°s ${isPhoto ? 'fotos' : 'videos'}`,
        variant: "destructive"
      });
      return;
    }

    if (files.length > availableSlots) {
      toast({
        title: "Demasiados archivos",
        description: `Solo se agregar√°n los primeros ${availableSlots} archivos`,
      });
    }

    // Limitar la cantidad de archivos que se pueden agregar
    const filesToAdd = files.slice(0, availableSlots);

    // Asegurarnos de que mediaItems es un array v√°lido
    const safeMediaItems = Array.isArray(mediaItems) ? mediaItems : [];

    // Verificar si hay alg√∫n elemento principal existente (con protecci√≥n adicional contra null/undefined)
    const hasMainItem = safeMediaItems.some(item => 
      item && item.isMain === true && item.deleted !== true && item.toDelete !== true
    );

    // Crear nuevos elementos de medios
    const newMediaItems = filesToAdd.map((file, idx): MediaItem => {
      // Para evitar archivos no soportados
      const isValidType = isPhoto 
        ? file.type.startsWith('image/') 
        : file.type.startsWith('video/');

      // Verificar el tama√±o (l√≠mite de 5MB para im√°genes, 10MB para videos)
      const maxSize = isPhoto ? 5 * 1024 * 1024 : 10 * 1024 * 1024;
      const isSizeValid = file.size <= maxSize;

      // Determinar si este elemento debe ser principal
      // Prioridad para fotos como elemento principal
      const shouldBeMain = !hasMainItem && 
                          ((isPhoto && idx === 0) || 
                           (safeMediaItems.length === 0 && idx === 0));

      let errorMsg;
      if (!isValidType) {
        errorMsg = `Formato no soportado: ${file.type}`;
      } else if (!isSizeValid) {
        errorMsg = `Archivo demasiado grande: ${(file.size / (1024 * 1024)).toFixed(1)}MB (m√°x. ${isPhoto ? '5MB' : '10MB'})`;
      }

      // Generar un ID √∫nico para este elemento
      const uniqueId = `new-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

      // Crear una URL de vista previa para el archivo (con manejo seguro)
      let previewUrl = '';
      try {
        previewUrl = URL.createObjectURL(file);
      } catch (error) {
        console.error("Error creando URL para vista previa:", error);
        errorMsg = errorMsg || "Error procesando archivo";
      }

      console.log(`Agregando nuevo elemento: ${uniqueId}, tipo: ${isPhoto ? 'photo' : 'video'}, archivo: ${file.name}`);

      // Crear objeto MediaItem con valores seguros
      return {
        id: uniqueId,
        type: isPhoto ? 'photo' : 'video',
        file,
        previewUrl, // URL temporal para vista previa
        isNew: true,
        order: safeMediaItems.length + idx,
        fileIndex: idx, // Agregar √≠ndice para seguimiento
        errorMessage: errorMsg,
        isMain: shouldBeMain && isValidType && isSizeValid
      };
    });

    // Filtrar archivos con errores que no se van a usar
    const validMediaItems = newMediaItems.filter(item => !item.errorMessage);
    const invalidMediaItems = newMediaItems.filter(item => !!item.errorMessage);

    // No mostramos alerta para archivos inv√°lidos
    // Simplemente los incluimos en la lista con su mensaje de error
    // para que el usuario pueda eliminarlos manualmente

    // Actualizar el estado con los archivos nuevos
    // IMPORTANTE: Usar la versi√≥n segura de mediaItems (safeMediaItems)
    const updatedMedia = [...safeMediaItems, ...newMediaItems];
    console.log("Media antes de ordenar:", updatedMedia.length, "elementos");

    // Asegurarse de que no haya null/undefined en el array
    const cleanMedia = updatedMedia.filter(item => item !== null && item !== undefined);

    // Asegurarse de que no haya duplicados (basados en el ID √∫nico)
    // Manejo adicional para casos donde los atributos puedan ser undefined
    const uniqueMedia = Array.from(new Map(
      cleanMedia.map(item => {
        const key = item.id || `${item.url || (item.file ? item.file.name : '') || Date.now()}`;
        return [key, item];
      })
    ).values());

    console.log("Media despu√©s de eliminar duplicados:", uniqueMedia);

    // Ordenar los elementos asegurando que cada uno tiene un orden v√°lido
    const sortedMedia = [...uniqueMedia].sort((a, b) => 
      (a.order || 0) - (b.order || 0)
    );

    // Asegurarse de que haya exactamente un elemento principal
    const mainItems = sortedMedia.filter(item => 
      item && item.isMain && !item.deleted && !item.toDelete && !item.errorMessage
    );

    let finalMedia = sortedMedia;

    // Si hay m√°s de un elemento principal, dejar solo uno (priorizar fotos)
    if (mainItems.length > 1) {
      console.log("Hay m√°s de un elemento principal, corrigiendo...");
      const mainPhoto = mainItems.find(item => item.type === 'photo');
      const itemToSetMain = mainPhoto || mainItems[0];
      console.log("Elemento principal seleccionado:", 
                 itemToSetMain.type, 
                 itemToSetMain.id || "sin ID");

      // Marcar solo uno como principal
      finalMedia = sortedMedia.map(item => ({
        ...item,
        isMain: item === itemToSetMain
      }));
    }
    // Si no hay ning√∫n elemento principal y hay elementos v√°lidos, marcar el primero como principal
    else if (mainItems.length === 0 && validMediaItems.length > 0) {
      console.log("No hay elemento principal, estableciendo uno autom√°ticamente");
      // Buscar primero una foto para establecer como principal
      const firstPhoto = sortedMedia.find(
        item => item.type === 'photo' && !item.deleted && !item.toDelete && !item.errorMessage
      );
      const firstValid = firstPhoto || sortedMedia.find(
        item => !item.deleted && !item.toDelete && !item.errorMessage
      );

      if (firstValid) {
        finalMedia = sortedMedia.map(item => ({
          ...item,
          isMain: item === firstValid
        }));
        console.log("Elemento principal establecido autom√°ticamente:", 
                   firstValid.type, 
                   firstValid.id || "sin ID");
      }
    }

    // Verificar cu√°ntos elementos principales tenemos ahora
    const finalMainItems = finalMedia.filter(item => 
      item.isMain && !item.deleted && !item.toDelete
    );
    console.log("Elementos principales despu√©s de procesar:", finalMainItems.length);

    // Actualizar el estado con los elementos procesados
    setMediaItems(finalMedia);

    // Actualizar los contadores
    if (isPhoto) {
      setPhotoCount(prev => prev + validMediaItems.length);
    } else {
      setVideoCount(prev => prev + validMediaItems.length);
    }

    // Notificar al componente padre
    onChange(finalMedia);

    // Limpiar el input de archivos para permitir seleccionar el mismo archivo nuevamente
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Manejar arrastrar y soltar (drag & drop)
  const handleDragStart = (index: number) => {
    if (disabled) return;
    setDraggedItem(index);
  };

  const handleDragOver = (index: number) => {
    if (disabled) return;
    setDragOverItem(index);
  };

  const handleDragEnd = () => {
    if (disabled || draggedItem === null || dragOverItem === null) return;

    // Los √≠ndices deben ser distintos y v√°lidos
    if (draggedItem === dragOverItem || 
        draggedItem < 0 || dragOverItem < 0 || 
        draggedItem >= mediaItems.length || dragOverItem >= mediaItems.length) {
      setDraggedItem(null);
      setDragOverItem(null);
      return;
    }

    // Crear una copia del array para no mutar el estado directamente
    const items = [...mediaItems];
    const draggedItemContent = items[draggedItem];

    // Protecci√≥n contra elementos null/undefined
    if (!draggedItemContent) {
      console.error("El elemento arrastrado es null o undefined");
      setDraggedItem(null);
      setDragOverItem(null);
      return;
    }

    // Eliminar elemento arrastrado
    items.splice(draggedItem, 1);

    // Insertar en nueva posici√≥n
    items.splice(dragOverItem, 0, draggedItemContent);

    // Actualizar √≠ndices/orden
    const reorderedItems = items.map((item, idx) => ({
      ...item,
      order: idx
    }));

    // Actualizar estado local
    setMediaItems(reorderedItems);

    // Notificar al componente padre
    onChange(reorderedItems);

    // Limpiar estados de arrastre
    setDraggedItem(null);
    setDragOverItem(null);
  };

  // Eliminar un medio
  const handleRemoveMedia = (indexToRemove: number) => {
    if (disabled) return;

    // Verificar que el √≠ndice es v√°lido y mediaItems existe
    if (!mediaItems || indexToRemove < 0 || indexToRemove >= mediaItems.length) {
      console.error("√çndice de eliminaci√≥n inv√°lido o no hay elementos multimedia");
      return;
    }

    const itemToRemove = mediaItems[indexToRemove];

    // Verificar que itemToRemove existe
    if (!itemToRemove) {
      console.error("No se pudo encontrar el elemento a eliminar");
      return;
    }

    console.log("Eliminando elemento multimedia:", itemToRemove);

    // Si es un elemento nuevo, simplemente lo eliminamos del array
    if (itemToRemove.isNew) {
      console.log("Eliminando elemento nuevo:", itemToRemove);

      // Filtrar el elemento a eliminar
      const updatedMedia = mediaItems.filter((item, index) => index !== indexToRemove);

      // Verificar si necesitamos asignar un nuevo elemento principal
      if (itemToRemove.isMain && updatedMedia.length > 0) {
        console.log("El elemento eliminado era principal, buscando nuevo elemento principal");

        // Buscar primero una foto para establecer como principal
        const firstPhoto = updatedMedia.find(
          item => item.type === 'photo' && !item.deleted && !item.toDelete
        );

        const firstValid = firstPhoto || updatedMedia.find(
          item => !item.deleted && !item.toDelete
        );

        if (firstValid) {
          console.log("Nuevo elemento principal:", firstValid);

          // Marcar el nuevo elemento como principal
          const finalMedia = updatedMedia.map(item => ({
            ...item,
            isMain: item === firstValid
          }));

          // Actualizar estado
          setMediaItems(finalMedia);

          // Actualizar contadores
          const newPhotoCount = finalMedia.filter(
            item => item.type === 'photo' && !item.deleted && !item.toDelete
          ).length;

          const newVideoCount = finalMedia.filter(
            item => item.type === 'video' && !item.deleted && !item.toDelete
          ).length;

          setPhotoCount(newPhotoCount);
          setVideoCount(newVideoCount);

          // Notificar al componente padre
          onChange(finalMedia);
          return;
        }
      }

      // Actualizar estado
      setMediaItems(updatedMedia);

      // Actualizar contadores
      const newPhotoCount = updatedMedia.filter(
        item => item.type === 'photo' && !item.deleted && !item.toDelete
      ).length;

      const newVideoCount = updatedMedia.filter(
        item => item.type === 'video' && !item.deleted && !item.toDelete
      ).length;

      setPhotoCount(newPhotoCount);
      setVideoCount(newVideoCount);

      // Notificar al componente padre
      onChange(updatedMedia);
    } 
    // Si es un elemento existente, lo marcamos para eliminaci√≥n
    else {
      console.log("Marcando elemento existente para eliminaci√≥n:", itemToRemove);

      // Crear una copia para no mutar el estado directamente
      const updatedMedia = mediaItems.map((item, index) => {
        if (index === indexToRemove) {
          return {
            ...item,
            deleted: true, // Para la UI (ocultar)
            toDelete: true, // Para el backend (eliminar de la DB)
            isMain: false // Nunca mostrar como principal un elemento eliminado
          };
        }
        return item;
      });

      // Verificar si necesitamos asignar un nuevo elemento principal
      if (itemToRemove.isMain) {
        console.log("El elemento marcado para eliminaci√≥n era principal, buscando reemplazo");

        // Elementos no eliminados
        const visibleItems = updatedMedia.filter(
          item => !item.deleted && !item.toDelete
        );

        if (visibleItems.length > 0) {
          // Buscar primero una foto para establecer como principal
          const firstPhoto = visibleItems.find(item => item.type === 'photo');
          const firstValid = firstPhoto || visibleItems[0];

          if (firstValid) {
            console.log("Nuevo elemento principal despu√©s de eliminaci√≥n:", firstValid);

            // Crear array final con el nuevo elemento principal
            const finalMedia = updatedMedia.map(item => {
              if (item === firstValid) {
                return {
                  ...item,
                  isMain: true
                };
              }
              return item;
            });

            // Actualizar estado
            setMediaItems(finalMedia);

            // Notificar al componente padre
            onChange(finalMedia);

            // Actualizar contadores
            const newPhotoCount = finalMedia.filter(
              item => item.type === 'photo' && !item.deleted && !item.toDelete
            ).length;

            const newVideoCount = finalMedia.filter(
              item => item.type === 'video' && !item.deleted && !item.toDelete
            ).length;

            setPhotoCount(newPhotoCount);
            setVideoCount(newVideoCount);

            return;
          }
        }
      }

      // Actualizar estado
      setMediaItems(updatedMedia);

      // Notificar al componente padre
      onChange(updatedMedia);

      // Actualizar contadores
      const newPhotoCount = updatedMedia.filter(
        item => item.type === 'photo' && !item.deleted && !item.toDelete
      ).length;

      const newVideoCount = updatedMedia.filter(
        item => item.type === 'video' && !item.deleted && !item.toDelete
      ).length;

      setPhotoCount(newPhotoCount);
      setVideoCount(newVideoCount);

      // Buscar elementos marcados para eliminaci√≥n para debugging
      const itemsToDelete = updatedMedia.filter(item => item.toDelete === true);
      if (itemsToDelete.length > 0) {
        console.log("Elementos marcados para eliminaci√≥n:", 
          itemsToDelete.map(item => ({ url: item.url, type: item.type })));
      }

      // Mostrar mensaje de confirmaci√≥n
      toast({
        title: "Elemento marcado para eliminaci√≥n",
        description: "El cambio se aplicar√° al guardar el formulario",
      });
    }
  };

  // Establecer un medio como principal
  const handleSetMainMedia = (indexToSetMain: number) => {
    // CONTROL 1: Si est√° deshabilitado, no hacer nada
    if (disabled) return;

    console.log("===== MARCANDO COMO PRINCIPAL =====");
    console.log("√çndice seleccionado:", indexToSetMain);

    // CONTROL 2: Verificar que mediaItems es un array v√°lido
    if (!Array.isArray(mediaItems)) {
      console.error("Error cr√≠tico: mediaItems no es un array");
      toast({
        title: "Error interno",
        description: "Ocurri√≥ un problema con la gesti√≥n de medios",
        variant: "destructive"
      });
      return;
    }

    // CONTROL 3: Verificar que el √≠ndice es v√°lido
    if (indexToSetMain < 0 || indexToSetMain >= mediaItems.length) {
      console.error("√çndice para establecer como principal fuera de rango:", indexToSetMain);
      return;
    }

    try {
      // PASO 1: Crear una copia profunda de todos los elementos para evitar mutaciones
      const clonedItems: MediaItem[] = mediaItems.map(item => {
        if (!item) {
          console.warn("Elemento null/undefined detectado en mediaItems");
          return null as unknown as MediaItem;
        }

        // Crear objeto base con tipo seguro
        const safeType = (item.type === 'photo' || item.type === 'video') ? item.type : 'photo';

        const newItem: MediaItem = {
          id: item.id || `id-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
          type: safeType,
          isMain: false, // Por defecto, ninguno es principal (se establecer√° despu√©s)
          order: typeof item.order === 'number' ? item.order : 0,
        };

        // Copiar propiedades opcionales solo si existen
        if (item.url) newItem.url = item.url;
        if (item.file) newItem.file = item.file;
        if (item.previewUrl) newItem.previewUrl = item.previewUrl;
        if (item.isNew) newItem.isNew = true;
        if (item.deleted) newItem.deleted = true;
        if (item.toDelete) newItem.toDelete = true;
        if (item.errorMessage) newItem.errorMessage = item.errorMessage;

        return newItem;
      }).filter(Boolean) as MediaItem[]; // Eliminar posibles null/undefined

      // PASO 2: Obtener una referencia segura al elemento a marcar como principal
      const itemToSetMain = clonedItems[indexToSetMain];

      // Verificaci√≥n adicional
      if (!itemToSetMain) {
        throw new Error("El elemento seleccionado no existe despu√©s de la clonaci√≥n");
      }

      // PASO 3: Verificar que el elemento cumple criterios para ser principal
      if (itemToSetMain.errorMessage) {
        console.warn("No se puede establecer como principal un elemento con errores");
        return;
      }

      if (itemToSetMain.deleted || itemToSetMain.toDelete) {
        console.warn("No se puede establecer como principal un elemento marcado para eliminaci√≥n");
        return;
      }

      // PASO 4: Marcar solo el elemento seleccionado como principal
      clonedItems.forEach((item, idx) => {
        item.isMain = (idx === indexToSetMain);
      });

      console.log("Estado actualizado - Elemento principal:", clonedItems[indexToSetMain].id);

      // Verificar elemento principal para debugging
      const mainItems = clonedItems.filter(item => item.isMain === true);
      console.log("¬øHay elemento principal expl√≠cito?", mainItems.length > 0);

      if (mainItems.length > 0) {
        console.log("ELEMENTO PRINCIPAL FINAL:", {
          tipo: mainItems[0].type || "desconocido",
          id: mainItems[0].id || "sin-id"
        });
      } else {
        console.error("¬°No se encontr√≥ un elemento principal despu√©s de la actualizaci√≥n!");
      }

      // PASO 5: Actualizar el estado y notificar al formulario padre
      setMediaItems(clonedItems);

      // IMPORTANTE: Usar una copia fresca para evitar mutaciones accidentales
      onChange([...clonedItems]);

      // PASO 6: Notificaci√≥n visual al usuario
      const mainItem = clonedItems[indexToSetMain];
      const itemName = mainItem && mainItem.file ? mainItem.file.name : 
                     (mainItem && mainItem.url ? 'Archivo seleccionado' : 'Elemento');

      toast({
        title: "Elemento principal actualizado",
        description: `${itemName} ser√° la imagen destacada`,
        duration: 3000
      });

      console.log("=== PROCESANDO onChange EN MEDIA MANAGER ===");
      console.log("¬øHay elemento principal expl√≠cito?", mainItems.length > 0);
      console.log("ELEMENTO PRINCIPAL FINAL:", {tipo: mainItems.length > 0 ? mainItems[0].type : 'ninguno'});
      console.log("=== FIN PROCESAMIENTO onChange ===");

    } catch (error) {
      // MANEJO DE ERRORES
      console.error("Error cr√≠tico al establecer elemento principal:", error);

      toast({
        title: "Error al establecer elemento principal",
        description: "Ha ocurrido un problema t√©cnico. Por favor, int√©ntalo de nuevo.",
        variant: "destructive",
        duration: 5000
      });
    }
  };

  // Renderizar miniatura para medio (foto o video)
  const renderMediaItem = (item: MediaItem, index: number) => {
    // Verificar si el item es v√°lido
    if (!item) {
      console.error(`Intentando renderizar un item undefined o null en el √≠ndice ${index}`);
      return null;
    }

    // No mostrar elementos eliminados
    if (item.deleted || item.toDelete) {
      return null;
    }

    return (
      <div 
        key={item.id || index}
        className={cn(
          "relative group border rounded-md overflow-hidden hover:shadow-md transition-all",
          "flex flex-col items-center justify-center",
          "min-w-[120px] w-[120px] h-[120px]",
          "bg-background",
          draggedItem === index ? "opacity-50 border-dashed" : "",
          dragOverItem === index ? "bg-accent" : "",
          item.errorMessage ? "border-destructive" : (item.isMain ? "border-primary border-2" : "border-border")
        )}
        draggable={!disabled}
        onDragStart={() => handleDragStart(index)}
        onDragOver={() => handleDragOver(index)}
        onDragEnd={handleDragEnd}
      >
        {/* Previsualizacion del medio */}
        <MediaPreview 
          file={item.file}
          url={item.url}
          previewUrl={item.previewUrl}
          type={item.type}
          className="w-full h-full object-cover"
        />

        {/* Acciones */}
        <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 bg-black/50 transition-opacity">
          {/* Bot√≥n para establecer como principal */}
          <Button
            type="button"
            onClick={() => handleSetMainMedia(index)}
            disabled={item.isMain || disabled}
            className={cn(
              "absolute top-1 left-1 h-7 w-7 p-0 rounded-full",
              item.isMain ? "bg-primary text-primary-foreground hover:bg-primary/90" : "bg-black/50 hover:bg-black/70"
            )}
            title="Establecer como principal"
          >
            {item.isMain ? <Star size={14} /> : <StarOff size={14} />}
          </Button>

          {/* Bot√≥n para eliminar */}
          <Button
            type="button"
            onClick={() => handleRemoveMedia(index)}
            disabled={disabled}
            className="absolute top-1 right-1 h-7 w-7 p-0 rounded-full bg-black/50 hover:bg-destructive/90"
            title="Eliminar"
          >
            <Trash size={14} />
          </Button>
        </div>

        {/* Icono de tipo */}
        <div className="absolute bottom-1 left-1 bg-black/50 text-white p-1 rounded-sm text-xs">
          {item.type === 'photo' ? <Image size={14} /> : <Video size={14} />}
        </div>

        {/* Indicador de elemento principal */}
        {item.isMain && (
          <Badge className="absolute bottom-1 right-1 text-xs bg-primary" variant="default">
            Principal
          </Badge>
        )}

        {/* Mensaje de error si lo hay */}
        {item.errorMessage && (
          <div className="absolute inset-0 flex items-center justify-center bg-black/70 text-white p-2 text-xs text-center">
            <div className="flex flex-col items-center space-y-1">
              <AlertCircle size={16} className="text-destructive" />
              <span>{item.errorMessage}</span>
            </div>
          </div>
        )}

        {/* Indicador de carga */}
        {item.uploading && (
          <div className="absolute inset-0 flex flex-col items-center justify-center bg-black/70 text-white">
            <Loader2 size={24} className="animate-spin mb-2" />
            <div className="text-xs">{item.uploadProgress || 0}%</div>
          </div>
        )}
      </div>
    );
  };

  // Conteo de elementos v√°lidos (no eliminados)
  const visibleItems = mediaItems.filter(item => !item.deleted && !item.toDelete);

  return (
    <div className="space-y-4">
      {/* Input oculto para seleccionar archivos */}
      <input 
        type="file" 
        ref={fileInputRef}
        className="hidden"
        onChange={handleFileChange}
        multiple
      />

      {/* √Årea de visualizaci√≥n de medios */}
      <div className="flex flex-wrap gap-3 min-h-[120px] border border-dashed rounded-md p-3">
        {visibleItems.length > 0 ? (
          visibleItems.map((item, index) => renderMediaItem(item, index))
        ) : (
          <div className="w-full h-full flex items-center justify-center text-muted-foreground text-sm py-8">
            No hay medios a√±adidos
          </div>
        )}
      </div>

      {/* Botones para agregar medios */}
      <div className="flex flex-wrap gap-2">
        <Button
          type="button"
          onClick={() => handleAddMedia(true)}
          disabled={disabled || photoCount >= maxPhotos}
          variant="outline"
          className="text-xs"
        >
          <Image className="mr-2" size={14} />
          A√±adir Foto ({photoCount}/{maxPhotos})
        </Button>

        <Button
          type="button"
          onClick={() => handleAddMedia(false)}
          disabled={disabled || videoCount >= maxVideos}
          variant="outline"
          className="text-xs"
        >
          <Video className="mr-2" size={14} />
          A√±adir Video ({videoCount}/{maxVideos})
        </Button>
      </div>
    </div>
  );
};

export default MediaManager;

import { useRef, useState, useEffect } from 'react';
import { cn } from '@/lib/utils';
import { useToast } from '@/hooks/use-toast';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Image, Video, Trash, Star, StarOff, X } from 'lucide-react';

export type MediaItem = {
  id?: string;
  type: 'photo' | 'video';
  url?: string; // URL para medios existentes
  file?: File; // File para nuevos medios
  previewUrl?: string; // URL temporal para vista previa (solo para UI)
  isMain?: boolean;
  isNew?: boolean;
  deleted?: boolean; // Marca para vista (ocultar de la UI)
  toDelete?: boolean; // Marca para enviar al servidor (eliminaci√≥n permanente)
  order?: number; // Para ordenamiento
  uploading?: boolean; // Estado de carga
  uploadProgress?: number; // Progreso de carga (0-100)
  errorMessage?: string; // Mensaje de error si falla la carga
  fileIndex?: number; // √çndice del archivo para seguimiento en el servidor
};

type MediaManagerProps = {
  existingMedia?: MediaItem[] | string; // Puede ser string para JSON o MediaItem[]
  onChange: (media: MediaItem[]) => void;
  maxPhotos?: number;
  maxVideos?: number;
  disabled?: boolean;
};

export const MediaManager = ({
  existingMedia = [],
  onChange,
  maxPhotos = 6,
  maxVideos = 3,
  disabled = false,
}: MediaManagerProps) => {
  console.log("üé¨ MediaManager RENDER START - Component is rendering");
  console.log("üé¨ MediaManager props:", { existingMedia: Array.isArray(existingMedia) ? existingMedia.length : typeof existingMedia, disabled, maxPhotos, maxVideos });

  const { toast } = useToast();
  const [mediaItems, setMediaItems] = useState<MediaItem[]>([]);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Al inicializar, procesar existingMedia
  useEffect(() => {
    console.log("üîÑ MediaManager useEffect TRIGGERED - existingMedia changed");
    console.log("üîÑ Current mediaItems.length before update:", mediaItems.length);
    console.log("üîÑ New existingMedia:", typeof existingMedia, existingMedia);

    // CRITICAL: Don't reset mediaItems if we already have new items that haven't been saved
    const hasUnsavedItems = mediaItems.some(item => item.isNew && item.file);
    if (hasUnsavedItems) {
      console.log("‚ö†Ô∏è PREVENTING RESET: MediaManager has unsaved new items, skipping existingMedia update");
      console.log("‚ö†Ô∏è Unsaved items:", mediaItems.filter(item => item.isNew && item.file).map(item => ({ id: item.id, fileName: item.file?.name })));
      return;
    }

    let initialItems: MediaItem[] = [];

    // Si es un array y tiene elementos, usarlo directamente
    if (Array.isArray(existingMedia) && existingMedia.length > 0) {
      initialItems = existingMedia.map(item => ({ ...item }));
      console.log("MediaManager: Media recibido como array:", initialItems.length, initialItems);
    }
    // Si es un string, intentar parsearlo como JSON
    else if (typeof existingMedia === 'string' && existingMedia.trim()) {
      try {
        initialItems = JSON.parse(existingMedia);
        console.log("MediaManager: Media parseado de JSON string:", initialItems.length, initialItems);
      } catch (e) {
        console.error('MediaManager: Error al parsear JSON:', e);
        console.log('MediaManager: String original:', existingMedia);
        setMediaItems([]);
        onChange([]);
        return;
      }
    }
    // Si no hay datos v√°lidos
    else {
      console.log("MediaManager: No hay media existente v√°lido");
      setMediaItems([]);
      onChange([]);
      return;
    }

    // Filtrar elementos nulos y undefined para evitar errores
    initialItems = initialItems.filter(item => item !== null && item !== undefined);

    // Asegurarse de que cada elemento tenga un orden
    const itemsWithOrder = initialItems.map((item, index) => ({
      ...item,
      order: item.order !== undefined ? item.order : index
    }));

    // Ordenar: primero el elemento principal, luego por order
    const sortedItems = [...itemsWithOrder].sort((a, b) => {
      // Si uno es principal y el otro no, el principal va primero
      if (a.isMain && !b.isMain) return -1;
      if (!a.isMain && b.isMain) return 1;
      // Si ambos son principales o ninguno es principal, ordenar por order
      return (a.order || 0) - (b.order || 0);
    });

    console.log("Media inicial ordenado:", sortedItems);

    // Asegurarse de que solo hay un elemento principal
    let hasMain = false;
    const finalItems = sortedItems.map(item => {
      if (item.isMain && !hasMain) {
        hasMain = true;
        return { ...item, isMain: true };
      }
      return { ...item, isMain: false };
    });

    // Si no hay un elemento principal, establecer el primero como principal
    if (!hasMain && finalItems.length > 0) {
      finalItems[0].isMain = true;
      console.log("Estableciendo el primer elemento como principal por defecto");
    }

    console.log("üîÑ SETTING INITIAL MEDIA from existingMedia:", finalItems.length);
    console.log("üîÑ EXISTING MEDIA DETAILS:", finalItems.map(item => ({ 
      id: item.id, 
      type: item.type, 
      isNew: !!item.isNew,
      hasUrl: !!item.url,
      hasFile: !!item.file
    })));

    setMediaItems(finalItems);

    // Importante: Asegurarnos de que el componente padre tenga el estado inicial correcto
    // Esta l√≠nea soluciona el problema del "undefined" al enviar el formulario
    onChange(finalItems);
  }, [existingMedia]);

  // Contar elementos visibles actuales
  const validItems = mediaItems.filter(item => item && typeof item === 'object');
  const visibleItems = validItems.filter(item => !item.deleted && !item.toDelete);
  const photoCount = visibleItems.filter(item => item.type === 'photo').length;
  const videoCount = visibleItems.filter(item => item.type === 'video').length;

  // Verificar si se pueden agregar m√°s elementos
  const canAddMorePhotos = photoCount < maxPhotos;
  const canAddMoreVideos = videoCount < maxVideos;

  console.log("MediaManager render counts:", {
    totalItems: mediaItems.length,
    validItems: validItems.length,
    visibleItems: visibleItems.length,
    photoCount,
    videoCount,
    canAddMorePhotos,
    canAddMoreVideos,
    disabled
  });

  // Funci√≥n para seleccionar archivos
  const handleSelectFiles = (acceptType: 'image/*' | 'video/*') => {
    console.log("=== FILE SELECTION STARTED ===");
    console.log("Accept type:", acceptType);
    console.log("Current media items before selection:", mediaItems.length);
    console.log("Disabled state:", disabled);

    if (disabled) {
      console.log("File selection disabled, returning");
      return;
    }

    if (fileInputRef.current) {
      console.log("File input ref found, setting accept and clicking");
      fileInputRef.current.accept = acceptType;
      fileInputRef.current.click();
    } else {
      console.error("File input ref not found!");
    }
  };

  // Procesar archivos seleccionados con validaci√≥n mejorada
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (!e.target.files || e.target.files.length === 0) return;

    try {
      const files = Array.from(e.target.files);
      const isImage = e.target.accept === 'image/*';
      const mediaType = isImage ? 'photo' : 'video';

      // Verificar si hay espacio para m√°s archivos
      const currentCount = mediaType === 'photo' ? photoCount : videoCount;
      const maxCount = mediaType === 'photo' ? maxPhotos : maxVideos;

      if (currentCount + files.length > maxCount) {
        toast({
          title: `Demasiados ${mediaType === 'photo' ? 'fotos' : 'videos'}`,
          description: `Solo puedes agregar hasta ${maxCount} ${mediaType === 'photo' ? 'fotos' : 'videos'}`,
          variant: "destructive"
        });

        // Limpiar input para que pueda seleccionar los mismos archivos de nuevo
        e.target.value = '';
        return;
      }

      // Procesar cada archivo
      const newItems: MediaItem[] = [];

      files.forEach(file => {
        // Validar tama√±o (10MB m√°ximo)
        if (file.size > 10 * 1024 * 1024) {
          toast({
            title: "Archivo demasiado grande",
            description: `${file.name} excede el l√≠mite de 10MB`,
            variant: "destructive"
          });
          return;
        }

        // Crear URL temporal para vista previa
        const previewUrl = URL.createObjectURL(file);

        // Crear nuevo item con verificaci√≥n de tipo
        const newItem: MediaItem = {
          id: `new-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
          type: mediaType,
          file,
          previewUrl,
          isNew: true,
          isMain: false, // Por defecto no es principal
          order: mediaItems.length + newItems.length
        };

        newItems.push(newItem);
      });

      // Si no hay elementos nuevos, no hacemos nada
      if (newItems.length === 0) {
        console.log("No se agregaron nuevos elementos");
        // Limpiar input para que pueda seleccionar los mismos archivos de nuevo
        e.target.value = '';
        return;
      }

      // Asegurarse de que mediaItems existe y es un array
      const currentItems = Array.isArray(mediaItems) ? [...mediaItems] : [];

      // Si no hay elementos en mediaItems o no hay ning√∫n elemento principal marcado, hacer el primero principal
      const hasMainItem = currentItems.some(item => item && item.isMain === true && !item.deleted && !item.toDelete);

      if (!hasMainItem && newItems.length > 0) {
        newItems[0].isMain = true;
        console.log("Marcando el primer elemento nuevo como principal");
      }

      // Combinar elementos actuales y nuevos con validaci√≥n adicional
      const updatedItems = [...currentItems, ...newItems].filter(item => item !== null && item !== undefined);

      console.log("=== MEDIA PROCESSING DEBUG ===");
      console.log("Current items count:", currentItems.length);
      console.log("New items count:", newItems.length);
      console.log("Updated items count:", updatedItems.length);
      console.log("New items details:", newItems.map(item => ({
        id: item.id,
        type: item.type,
        fileName: item.file?.name,
        isNew: item.isNew,
        hasPreviewUrl: !!item.previewUrl
      })));
      console.log("Updated items details:", updatedItems.map(item => ({
        id: item.id,
        type: item.type,
        isNew: item.isNew,
        hasUrl: !!item.url,
        hasPreviewUrl: !!item.previewUrl
      })));

      // Si el array est√° vac√≠o despu√©s de los filtros, proporcionar un array vac√≠o v√°lido
      const safeItems = updatedItems.length > 0 ? updatedItems : [];

      console.log("üî• ADDING NEW FILES - Setting media items to:", safeItems.length);
      console.log("üî• ADDING NEW FILES - Details:", safeItems.map(item => ({ 
        id: item.id, 
        type: item.type, 
        isNew: item.isNew, 
        fileName: item.file?.name || 'no file',
        hasUrl: !!item.url 
      })));

      setMediaItems(safeItems);
      // Garantizar que siempre se pase un array v√°lido al llamar a onChange
      onChange(safeItems);

      console.log("üî• ADDING NEW FILES - onChange called with", safeItems.length, "items");

      // Limpiar input para que pueda seleccionar los mismos archivos de nuevo
      e.target.value = '';

      // Mostrar mensaje de √©xito
      toast({
        title: "Archivos agregados",
        description: `Se han agregado ${newItems.length} ${mediaType === 'photo' ? 'fotos' : 'videos'}`,
      });
    } catch (error) {
      console.error("Error al procesar archivos:", error);
      toast({
        title: "Error al procesar archivos",
        description: "No se pudieron procesar algunos archivos. Intenta nuevamente.",
        variant: "destructive"
      });

      // Limpiar input para que pueda seleccionar los mismos archivos de nuevo
      if (e.target) e.target.value = '';

      // En caso de error, asegur√©monos de que onChange reciba un array v√°lido (el estado actual)
      onChange(Array.isArray(mediaItems) ? mediaItems : []);
    }
  };

  // Funci√≥n mejorada para establecer un elemento como principal
  const handleSetMain = (index: number) => {
    console.log("===== MARCANDO COMO PRINCIPAL =====");
    console.log("√çndice seleccionado:", index);

    if (disabled) return;

    // Validaci√≥n inicial robusta
    if (!Array.isArray(mediaItems)) {
      console.error("mediaItems no es un array v√°lido");
      onChange([]);
      return;
    }

    if (index < 0 || index >= mediaItems.length) {
      console.error("√çndice inv√°lido:", index);
      onChange([...mediaItems]);
      return;
    }

    const targetItem = mediaItems[index];

    // Verificaciones de validez del elemento objetivo
    if (!targetItem || typeof targetItem !== 'object') {
      console.error("Elemento no v√°lido en el √≠ndice:", index);
      onChange([...mediaItems]);
      return;
    }

    // No se puede marcar como principal un elemento eliminado
    if (targetItem.deleted || targetItem.toDelete) {
      toast({
        title: "Elemento no v√°lido",
        description: "No se puede establecer como principal un elemento eliminado",
        variant: "destructive"
      });
      return;
    }

    // No se puede marcar como principal un elemento con errores
    if (targetItem.errorMessage) {
      toast({
        title: "Elemento con errores",
        description: "No se puede establecer como principal un elemento que tiene errores",
        variant: "destructive"
      });
      return;
    }

    // Debe tener contenido v√°lido (URL, file o previewUrl)
    if (!targetItem.url && !targetItem.file && !targetItem.previewUrl) {
      toast({
        title: "Elemento sin contenido",
        description: "Este elemento no tiene imagen o video v√°lido",
        variant: "destructive"
      });
      return;
    }

    try {
      // Filtrar elementos v√°lidos y actualizar estado
      const validItems = mediaItems.filter(item => 
        item !== null && 
        item !== undefined && 
        typeof item === 'object'
      );

      // Asegurar que solo el elemento target sea principal
      const updatedItems = validItems.map((item, i) => ({
        ...item,
        isMain: i === index
      }));

      // Reorganizar: mover el elemento principal al inicio
      const reorderedItems = [...updatedItems].sort((a, b) => {
        // Si uno es principal y el otro no, el principal va primero
        if (a.isMain && !b.isMain) return -1;
        if (!a.isMain && b.isMain) return 1;
        // Si ambos son principales o ninguno es principal, mantener orden actual
        return 0;
      });

      console.log("Estado actualizado - Elemento principal:", 
        reorderedItems.find(item => item.isMain)?.id || "ninguno");
      console.log("Elemento principal movido a posici√≥n 0");

      // Actualizar estado y notificar cambios
      setMediaItems(reorderedItems);
      onChange(reorderedItems);

      // Mostrar confirmaci√≥n al usuario
      toast({
        title: "Elemento principal actualizado",
        description: "Este elemento ser√° la imagen destacada del evento",
      });

    } catch (error) {
      console.error("Error al establecer elemento principal:", error);
      toast({
        title: "Error al actualizar",
        description: "No se pudo establecer el elemento principal",
        variant: "destructive"
      });

      // Mantener estado actual en caso de error
      onChange(Array.isArray(mediaItems) ? [...mediaItems] : []);
    }
  };

  // Funci√≥n para eliminar un elemento
  const handleRemove = (index: number) => {
    if (disabled) return;

    // Verificar que mediaItems es un array v√°lido
    if (!Array.isArray(mediaItems)) {
      console.error("mediaItems no es un array v√°lido al intentar eliminar");
      onChange([]);
      return;
    }

    // Verificar que el √≠ndice es v√°lido
    if (index < 0 || index >= mediaItems.length) {
      console.error("√çndice inv√°lido para eliminar:", index);
      onChange([...mediaItems]);
      return;
    }

    try {
      const itemToRemove = mediaItems[index];

      // Verificar que el elemento existe
      if (!itemToRemove) {
        console.error("Elemento no encontrado para eliminar");
        onChange([...mediaItems]);
        return;
      }

      // Si es un elemento nuevo, eliminarlo completamente
      if (itemToRemove.isNew) {
        // Liberar URL de vista previa
        if (itemToRemove.previewUrl) {
          try {
            URL.revokeObjectURL(itemToRemove.previewUrl);
          } catch (e) {
            console.error("Error al liberar URL de vista previa:", e);
          }
        }

        // Filtrar el elemento con validaci√≥n adicional
        const updatedItems = mediaItems.filter((item, i) => i !== index && item !== null && item !== undefined);

        // Si el elemento eliminado era el principal, establecer otro como principal
        if (itemToRemove.isMain && updatedItems.length > 0) {
          updatedItems[0].isMain = true;
        }

        // Actualizar estado
        setMediaItems(updatedItems);
        onChange(updatedItems);
      } 
      // Si es un elemento existente, marcarlo para eliminaci√≥n
      else {
        const updatedItems = mediaItems.map((item, i) => {
          if (item === null || item === undefined) {
            return null; // Ser√° filtrado despu√©s
          }

          if (i === index) {
            return {
              ...item,
              deleted: true,
              toDelete: true,
              isMain: false // Ya no puede ser principal
            };
          }
          return item;
        }).filter(item => item !== null && item !== undefined); // Filtrar elementos inv√°lidos

        // Si el elemento eliminado era el principal, establecer otro como principal
        if (itemToRemove.isMain) {
          const visibleItems = updatedItems.filter(item => !item.deleted && !item.toDelete);
          if (visibleItems.length > 0) {
            // Buscar el primer elemento visible y establecerlo como principal
            const firstVisibleIndex = updatedItems.findIndex(
              item => !item.deleted && !item.toDelete
            );

            if (firstVisibleIndex >= 0) {
              updatedItems[firstVisibleIndex].isMain = true;
            }
          }
        }

        // Actualizar estado
        setMediaItems(updatedItems);
        onChange(updatedItems);
      }

      // Mostrar notificaci√≥n
      toast({
        title: "Elemento eliminado",
        description: "El archivo ha sido eliminado"
      });
    } catch (error) {
      console.error("Error al eliminar elemento:", error);
      toast({
        title: "Error al eliminar",
        description: "No se pudo eliminar el elemento",
        variant: "destructive"
      });

      // En caso de error, asegurar que devolvemos un array v√°lido
      onChange(Array.isArray(mediaItems) ? [...mediaItems] : []);
    }
  };

  // Verificaci√≥n mejorada: asegurar que siempre hay un elemento principal v√°lido
  useEffect(() => {
    if (!Array.isArray(mediaItems) || mediaItems.length === 0) return;

    const visibleItems = mediaItems.filter(item => 
      item && !item.deleted && !item.toDelete && (item.url || item.file || item.previewUrl)
    );

    if (visibleItems.length === 0) return;

    const hasValidMainItem = visibleItems.some(item => item.isMain === true);

    // Solo intervenir si NO hay elemento principal v√°lido
    if (!hasValidMainItem) {
      console.log("Auto-asignando elemento principal - no hay uno v√°lido");

      const firstValidIndex = mediaItems.findIndex(item => 
        item && !item.deleted && !item.toDelete && (item.url || item.file || item.previewUrl)
      );

      if (firstValidIndex >= 0) {
        const updatedItems = mediaItems.map((item, i) => ({
          ...item,
          isMain: i === firstValidIndex
        }));

        setMediaItems(updatedItems);
        onChange(updatedItems);

        console.log("Elemento principal auto-asignado:", {
          √≠ndice: firstValidIndex,
          tipo: updatedItems[firstValidIndex].type
        });
      }
    }
  }, [mediaItems, onChange]);

  return (
    <div className="w-full">
      {/* Contador de archivos */}
      <div className="flex justify-between mb-2 text-sm text-gray-600">
        <div>Fotos: {photoCount}/{maxPhotos}</div>
        <div>Videos: {videoCount}/{maxVideos}</div>
      </div>

      {/* Contenedor de miniaturas */}
      <div className="grid grid-cols-2 gap-2 mb-4 max-w-full">
        {mediaItems.map((item, index) => {
          // Validar elemento antes de procesarlo
          if (!item || typeof item !== 'object') {
            console.warn(`Invalid item at index ${index}:`, item);
            return null;
          }

          // No mostrar elementos eliminados
          if (item.deleted || item.toDelete) return null;

          console.log(`Rendering media item ${index}:`, {
            id: item.id,
            type: item.type,
            hasUrl: !!item.url,
            hasPreviewUrl: !!item.previewUrl,
            isMain: item.isMain,
            isNew: item.isNew
          });

          return (
            <div 
              key={item.id || index}
              className={cn(
                "relative group aspect-square rounded-md overflow-hidden shadow-sm",
                item.isMain ? "ring-2 ring-yellow-500" : "",
                disabled ? "opacity-70 pointer-events-none" : ""
              )}
            >
              {/* Contenido del medio */}
              {item && item.type ? (
                item.type === 'photo' ? (
                  // Foto
                  <img 
                    src={item.previewUrl || item.url}
                    alt={`Foto ${index + 1}`}
                    className="w-full h-full object-cover"
                    onError={(e) => {
                      e.currentTarget.src = 'data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="100" height="100" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><circle cx="12" cy="12" r="10"/><line x1="15" y1="9" x2="9" y2="15"/><line x1="9" y1="9" x2="15" y2="15"/></svg>';
                    }}
                  />
                ) : (
                  // Video
                  <video 
                    src={item.previewUrl || item.url}
                    className="w-full h-full object-cover"
                    muted
                  />
                )
              ) : (
                // Fallback por si item o item.type no existe
                <div className="w-full h-full flex items-center justify-center bg-gray-200">
                  <span className="text-gray-500 text-xs">Archivo no disponible</span>
                </div>
              )}

              {/* Indicador de tipo */}
              {item && item.type && (
                <div className="absolute bottom-1 right-1 bg-black/50 rounded-full p-1">
                  {item.type === 'photo' ? (
                    <Image size={14} className="text-white" />
                  ) : (
                    <Video size={14} className="text-white" />
                  )}
                </div>
              )}

              {/* Controles superpuestos */}
              {item && (
                <div className="absolute top-0 left-0 right-0 flex justify-between p-1">
                  {/* Bot√≥n para eliminar */}
                  <button
                    type="button"
                    className="bg-red-500 text-white rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity"
                    onClick={() => handleRemove(index)}
                  >
                    <Trash size={14} />
                  </button>

                  {/* Bot√≥n para establecer como principal */}
                  <button
                    type="button"
                    className={cn(
                      "rounded-full p-1 transition-opacity",
                      item.isMain 
                        ? "bg-yellow-500 text-white opacity-100" 
                        : "bg-black/50 text-white opacity-0 group-hover:opacity-100"
                    )}
                    onClick={() => handleSetMain(index)}
                  >
                    {item.isMain ? <Star size={14} /> : <StarOff size={14} />}
                  </button>
                </div>
              )}

              {/* Etiqueta de principal */}
              {item && item.isMain && (
                <div className="absolute top-1 left-1/2 -translate-x-1/2 bg-yellow-500 text-white px-2 py-0.5 rounded text-xs font-medium shadow">
                  Imagen principal
                </div>
              )}

              {/* Estado de carga */}
              {item && item.uploading && (
                <div className="absolute inset-0 bg-black/70 flex flex-col items-center justify-center">
                  <div className="text-white text-xs mb-2">Subiendo...</div>
                  <Progress value={item.uploadProgress || 0} className="w-4/5 h-1" />
                </div>
              )}

              {/* Mensaje de error */}
              {item && item.errorMessage && (
                <div className="absolute inset-0 bg-red-500/80 flex items-center justify-center p-2">
                  <div className="text-white text-xs text-center">{item.errorMessage}</div>
                </div>
              )}
            </div>
          );
        })}
      </div>

      {/* Botones para agregar - Uno debajo del otro */}
      <div className="flex flex-col gap-2">
        <Button
          type="button"
          variant="outline"
          onClick={(e) => {
            console.log("=== BUTTON CLICK EVENT ===");
            console.log("Button clicked, event:", e.type);
            console.log("Can add more photos:", canAddMorePhotos);
            console.log("Disabled state:", disabled);
            console.log("Photo count:", photoCount, "max:", maxPhotos);

            e.preventDefault();
            e.stopPropagation();
            handleSelectFiles('image/*');
          }}
          disabled={!canAddMorePhotos || disabled}
          className="w-full"
        >
          <Image className="mr-2 h-4 w-4" />
          Agregar fotos
          <span className="ml-1 opacity-60">{photoCount}/{maxPhotos}</span>
        </Button>

        <Button
          type="button"
          variant="outline"
          onClick={() => handleSelectFiles('video/*')}
          disabled={!canAddMoreVideos || disabled}
          className="w-full"
        >
          <Video className="mr-2 h-4 w-4" />
          Agregar videos
          <span className="ml-1 opacity-60">{videoCount}/{maxVideos}</span>
        </Button>
      </div>

      {/* L√≠mites y consejos */}
      <div className="mt-2 text-xs text-gray-500">
        <ul className="list-disc list-inside space-y-1">
          <li>L√≠mites: hasta {maxPhotos} fotos (m√°x. 10MB c/u) y {maxVideos} videos (m√°x. 10MB c/u)</li>
          <li>Para destacar una imagen como principal, haz clic en el bot√≥n de estrella</li>
        </ul>
      </div>

      {/* Input oculto para seleccionar archivos */}
      <input
        type="file"
        ref={fileInputRef}
        className="hidden"
        onChange={(e) => {
          console.log("=== FILE INPUT CHANGE EVENT ===");
          console.log("Files selected:", e.target.files?.length || 0);
          if (e.target.files) {
            const files = Array.from(e.target.files);
            console.log("Files details:", files.map(f => ({ name: f.name, type: f.type, size: f.size })));
          }
          handleFileChange(e);
        }}
        multiple
      />
    </div>
  );
};

// Exportaci√≥n predeterminada para compatibilidad con las importaciones existentes
export default MediaManager;

import React, { useState, useEffect, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Trash, Video, Image, Star, StarOff, GripVertical, AlertCircle, Loader2 } from 'lucide-react';
import { cn } from '@/lib/utils';
import { useToast } from '@/hooks/use-toast';
import MediaPreview from './media-preview';

export type MediaItem = {
  id?: string;
  type: 'photo' | 'video';
  url?: string; // URL para medios existentes
  file?: File; // File para nuevos medios
  previewUrl?: string; // URL temporal para vista previa (solo para UI)
  isMain?: boolean;
  isNew?: boolean;
  deleted?: boolean; // Marca para vista (ocultar de la UI)
  toDelete?: boolean; // Marca para enviar al servidor (eliminaci√≥n permanente)
  order?: number; // Para ordenamiento
  uploading?: boolean; // Estado de carga
  uploadProgress?: number; // Progreso de carga (0-100)
  errorMessage?: string; // Mensaje de error si falla la carga
  fileIndex?: number; // √çndice del archivo para seguimiento en el servidor
};

type MediaManagerProps = {
  existingMedia?: MediaItem[] | string; // Puede ser string para JSON o MediaItem[]
  onChange: (media: MediaItem[]) => void;
  maxPhotos?: number;
  maxVideos?: number;
  disabled?: boolean;
};

export const MediaManager: React.FC<MediaManagerProps> = ({
  existingMedia = [],
  onChange,
  maxPhotos = 6,
  maxVideos = 3,
  disabled = false,
}) => {
  const { toast } = useToast();
  // Inicializaci√≥n segura de mediaItems
  const [mediaItems, setMediaItems] = useState<MediaItem[]>(() => {
    // Manejar caso donde existingMedia es undefined o null
    if (existingMedia === undefined || existingMedia === null) {
      console.log("existingMedia es undefined/null, inicializando como array vac√≠o");
      return [];
    }

    // Manejar caso donde existingMedia es string
    if (typeof existingMedia === 'string') {
      try {
        // Si es string vac√≠o, devolver array vac√≠o
        if (!existingMedia.trim()) {
          return [];
        }

        const parsed = JSON.parse(existingMedia);

        // Verificar que el resultado sea un array
        if (!Array.isArray(parsed)) {
          console.warn("existingMedia parseado no es un array:", parsed);
          return [];
        }

        // Filtrar elementos inv√°lidos
        return parsed.filter(item => item !== null && item !== undefined);
      } catch (e) {
        console.error('Error parsing media JSON:', e);
        return [];
      }
    }

    // Si es array, asegurarse de que todos los elementos son v√°lidos
    if (Array.isArray(existingMedia)) {
      return existingMedia.filter(item => item !== null && item !== undefined);
    }

    // Caso por defecto
    console.warn("existingMedia no es un tipo reconocido:", typeof existingMedia);
    return [];
  });

  const [photoCount, setPhotoCount] = useState(0);
  const [videoCount, setVideoCount] = useState(0);
  const [draggedItem, setDraggedItem] = useState<number | null>(null);
  const [dragOverItem, setDragOverItem] = useState<number | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Actualizar los contadores cuando cambia la lista de medios
  useEffect(() => {
    if (!Array.isArray(mediaItems)) {
      console.error("mediaItems no es un array en useEffect");
      return;
    }

    // Contar fotos y videos v√°lidos (no eliminados o marcados para eliminar)
    const photos = mediaItems.filter(
      item => item && item.type === 'photo' && !item.deleted && !item.toDelete
    );
    const videos = mediaItems.filter(
      item => item && item.type === 'video' && !item.deleted && !item.toDelete
    );

    setPhotoCount(photos.length);
    setVideoCount(videos.length);
  }, [mediaItems]);

  // Abrir el selector de archivos
  const handleAddMedia = (isPhoto: boolean) => {
    if (disabled) return;

    // Verificar si hay espacio disponible
    if (isPhoto && photoCount >= maxPhotos) {
      toast({
        title: "L√≠mite alcanzado",
        description: `No puedes agregar m√°s de ${maxPhotos} fotos`,
        variant: "destructive"
      });
      return;
    }

    if (!isPhoto && videoCount >= maxVideos) {
      toast({
        title: "L√≠mite alcanzado",
        description: `No puedes agregar m√°s de ${maxVideos} videos`,
        variant: "destructive"
      });
      return;
    }

    // Verificar y crear el input de archivos si es necesario
    if (!fileInputRef.current) {
      console.error("Input de archivos no encontrado");
      return;
    }

    // Establecer los tipos de archivos aceptados
    fileInputRef.current.accept = isPhoto ? 'image/*' : 'video/*';
    fileInputRef.current.dataset.type = isPhoto ? 'photo' : 'video';

    // Abrir el selector de archivos
    fileInputRef.current.click();
  };

  // Manejar la selecci√≥n de archivos
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (disabled) return;

    const files = Array.from(e.target.files || []);
    if (files.length === 0) return;

    const isPhoto = e.target.dataset.type === 'photo';
    console.log(`Agregando ${files.length} ${isPhoto ? 'fotos' : 'videos'}`);

    // Calcular espacio disponible
    const currentCount = isPhoto ? photoCount : videoCount;
    const maxAllowed = isPhoto ? maxPhotos : maxVideos;
    const availableSlots = Math.max(0, maxAllowed - currentCount);

    if (availableSlots === 0) {
      toast({
        title: "L√≠mite alcanzado",
        description: `No puedes agregar m√°s ${isPhoto ? 'fotos' : 'videos'}`,
        variant: "destructive"
      });
      return;
    }

    if (files.length > availableSlots) {
      toast({
        title: "Demasiados archivos",
        description: `Solo se agregar√°n los primeros ${availableSlots} archivos`,
      });
    }

    // Limitar la cantidad de archivos que se pueden agregar
    const filesToAdd = files.slice(0, availableSlots);

    // Asegurarnos de que mediaItems es un array v√°lido
    const safeMediaItems = Array.isArray(mediaItems) ? mediaItems : [];

    // Verificar si hay alg√∫n elemento principal existente (con protecci√≥n adicional contra null/undefined)
    const hasMainItem = safeMediaItems.some(item => 
      item && item.isMain === true && item.deleted !== true && item.toDelete !== true
    );

    // Crear nuevos elementos de medios
    const newMediaItems = filesToAdd.map((file, idx): MediaItem => {
      // Para evitar archivos no soportados
      const isValidType = isPhoto 
        ? file.type.startsWith('image/') 
        : file.type.startsWith('video/');

      // Verificar el tama√±o (l√≠mite de 5MB para im√°genes, 10MB para videos)
      const maxSize = isPhoto ? 5 * 1024 * 1024 : 10 * 1024 * 1024;
      const isSizeValid = file.size <= maxSize;

      // Determinar si este elemento debe ser principal
      // Prioridad para fotos como elemento principal
      const shouldBeMain = !hasMainItem && 
                          ((isPhoto && idx === 0) || 
                           (safeMediaItems.length === 0 && idx === 0));

      let errorMsg;
      if (!isValidType) {
        errorMsg = `Formato no soportado: ${file.type}`;
      } else if (!isSizeValid) {
        errorMsg = `Archivo demasiado grande: ${(file.size / (1024 * 1024)).toFixed(1)}MB (m√°x. ${isPhoto ? '5MB' : '10MB'})`;
      }

      // Generar un ID √∫nico para este elemento
      const uniqueId = `new-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

      // Crear una URL de vista previa para el archivo (con manejo seguro)
      let previewUrl = '';
      try {
        previewUrl = URL.createObjectURL(file);
      } catch (error) {
        console.error("Error creando URL para vista previa:", error);
        errorMsg = errorMsg || "Error procesando archivo";
      }

      console.log(`Agregando nuevo elemento: ${uniqueId}, tipo: ${isPhoto ? 'photo' : 'video'}, archivo: ${file.name}`);

      // Crear objeto MediaItem con valores seguros
      return {
        id: uniqueId,
        type: isPhoto ? 'photo' : 'video',
        file,
        previewUrl, // URL temporal para vista previa
        isNew: true,
        order: safeMediaItems.length + idx,
        fileIndex: idx, // Agregar √≠ndice para seguimiento
        errorMessage: errorMsg,
        isMain: shouldBeMain && isValidType && isSizeValid
      };
    });

    // Filtrar archivos con errores que no se van a usar
    const validMediaItems = newMediaItems.filter(item => !item.errorMessage);
    const invalidMediaItems = newMediaItems.filter(item => !!item.errorMessage);

    // No mostramos alerta para archivos inv√°lidos
    // Simplemente los incluimos en la lista con su mensaje de error
    // para que el usuario pueda eliminarlos manualmente

    // Actualizar el estado con los archivos nuevos
    // IMPORTANTE: Usar la versi√≥n segura de mediaItems (safeMediaItems)
    const updatedMedia = [...safeMediaItems, ...newMediaItems];
    console.log("Media antes de ordenar:", updatedMedia.length, "elementos");

    // Asegurarse de que no haya null/undefined en el array
    const cleanMedia = updatedMedia.filter(item => item !== null && item !== undefined);

    // Asegurarse de que no haya duplicados (basados en el ID √∫nico)
    // Manejo adicional para casos donde los atributos puedan ser undefined
    const uniqueMedia = Array.from(new Map(
      cleanMedia.map(item => {
        const key = item.id || `${item.url || (item.file ? item.file.name : '') || Date.now()}`;
        return [key, item];
      })
    ).values());

    console.log("Media despu√©s de eliminar duplicados:", uniqueMedia);

    // Ordenar los elementos asegurando que cada uno tiene un orden v√°lido
    const sortedMedia = [...uniqueMedia].sort((a, b) => 
      (a.order || 0) - (b.order || 0)
    );

    // Asegurarse de que haya exactamente un elemento principal
    const mainItems = sortedMedia.filter(item => 
      item && item.isMain && !item.deleted && !item.toDelete && !item.errorMessage
    );

    let finalMedia = sortedMedia;

    // Si hay m√°s de un elemento principal, dejar solo uno (priorizar fotos)
    if (mainItems.length > 1) {
      console.log("Hay m√°s de un elemento principal, corrigiendo...");
      const mainPhoto = mainItems.find(item => item.type === 'photo');
      const itemToSetMain = mainPhoto || mainItems[0];
      console.log("Elemento principal seleccionado:", 
                 itemToSetMain.type, 
                 itemToSetMain.id || "sin ID");

      // Marcar solo uno como principal
      finalMedia = sortedMedia.map(item => ({
        ...item,
        isMain: item === itemToSetMain
      }));
    }
    // Si no hay ning√∫n elemento principal y hay elementos v√°lidos, marcar el primero como principal
    else if (mainItems.length === 0 && validMediaItems.length > 0) {
      console.log("No hay elemento principal, estableciendo uno autom√°ticamente");
      // Buscar primero una foto para establecer como principal
      const firstPhoto = sortedMedia.find(
        item => item.type === 'photo' && !item.deleted && !item.toDelete && !item.errorMessage
      );
      const firstValid = firstPhoto || sortedMedia.find(
        item => !item.deleted && !item.toDelete && !item.errorMessage
      );

      if (firstValid) {
        finalMedia = sortedMedia.map(item => ({
          ...item,
          isMain: item === firstValid
        }));
        console.log("Elemento principal establecido autom√°ticamente:", 
                   firstValid.type, 
                   firstValid.id || "sin ID");
      }
    }

    // Verificar cu√°ntos elementos principales tenemos ahora
    const finalMainItems = finalMedia.filter(item => 
      item.isMain && !item.deleted && !item.toDelete
    );
    console.log("Elementos principales despu√©s de procesar:", finalMainItems.length);

    // Actualizar el estado con los elementos procesados
    setMediaItems(finalMedia);

    // Actualizar los contadores
    if (isPhoto) {
      setPhotoCount(prev => prev + validMediaItems.length);
    } else {
      setVideoCount(prev => prev + validMediaItems.length);
    }

    // Notificar al componente padre
    onChange(finalMedia);

    // Limpiar el input de archivos para permitir seleccionar el mismo archivo nuevamente
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Manejar arrastrar y soltar (drag & drop)
  const handleDragStart = (index: number) => {
    if (disabled) return;
    setDraggedItem(index);
  };

  const handleDragOver = (index: number) => {
    if (disabled) return;
    setDragOverItem(index);
  };

  const handleDragEnd = () => {
    if (disabled || draggedItem === null || dragOverItem === null) return;

    // Los √≠ndices deben ser distintos y v√°lidos
    if (draggedItem === dragOverItem || 
        draggedItem < 0 || dragOverItem < 0 || 
        draggedItem >= mediaItems.length || dragOverItem >= mediaItems.length) {
      setDraggedItem(null);
      setDragOverItem(null);
      return;
    }

    // Crear una copia del array para no mutar el estado directamente
    const items = [...mediaItems];
    const draggedItemContent = items[draggedItem];

    // Protecci√≥n contra elementos null/undefined
    if (!draggedItemContent) {
      console.error("El elemento arrastrado es null o undefined");
      setDraggedItem(null);
      setDragOverItem(null);
      return;
    }

    // Eliminar elemento arrastrado
    items.splice(draggedItem, 1);

    // Insertar en nueva posici√≥n
    items.splice(dragOverItem, 0, draggedItemContent);

    // Actualizar √≠ndices/orden
    const reorderedItems = items.map((item, idx) => ({
      ...item,
      order: idx
    }));

    // Actualizar estado local
    setMediaItems(reorderedItems);

    // Notificar al componente padre
    onChange(reorderedItems);

    // Limpiar estados de arrastre
    setDraggedItem(null);
    setDragOverItem(null);
  };

  // Eliminar un medio
  const handleRemoveMedia = (indexToRemove: number) => {
    if (disabled) return;

    // Verificar que el √≠ndice es v√°lido y mediaItems existe
    if (!mediaItems || indexToRemove < 0 || indexToRemove >= mediaItems.length) {
      console.error("√çndice de eliminaci√≥n inv√°lido o no hay elementos multimedia");
      return;
    }

    const itemToRemove = mediaItems[indexToRemove];

    // Verificar que itemToRemove existe
    if (!itemToRemove) {
      console.error("No se pudo encontrar el elemento a eliminar");
      return;
    }

    console.log("Eliminando elemento multimedia:", itemToRemove);

    // Si es un elemento nuevo, simplemente lo eliminamos del array
    if (itemToRemove.isNew) {
      console.log("Eliminando elemento nuevo:", itemToRemove);

      // Filtrar el elemento a eliminar
      const updatedMedia = mediaItems.filter((item, index) => index !== indexToRemove);

      // Verificar si necesitamos asignar un nuevo elemento principal
      if (itemToRemove.isMain && updatedMedia.length > 0) {
        console.log("El elemento eliminado era principal, buscando nuevo elemento principal");

        // Buscar primero una foto para establecer como principal
        const firstPhoto = updatedMedia.find(
          item => item.type === 'photo' && !item.deleted && !item.toDelete
        );

        const firstValid = firstPhoto || updatedMedia.find(
          item => !item.deleted && !item.toDelete
        );

        if (firstValid) {
          console.log("Nuevo elemento principal:", firstValid);

          // Marcar el nuevo elemento como principal
          const finalMedia = updatedMedia.map(item => ({
            ...item,
            isMain: item === firstValid
          }));

          // Actualizar estado
          setMediaItems(finalMedia);

          // Actualizar contadores
          const newPhotoCount = finalMedia.filter(
            item => item.type === 'photo' && !item.deleted && !item.toDelete
          ).length;

          const newVideoCount = finalMedia.filter(
            item => item.type === 'video' && !item.deleted && !item.toDelete
          ).length;

          setPhotoCount(newPhotoCount);
          setVideoCount(newVideoCount);

          // Notificar al componente padre
          onChange(finalMedia);
          return;
        }
      }

      // Actualizar estado
      setMediaItems(updatedMedia);

      // Actualizar contadores
      const newPhotoCount = updatedMedia.filter(
        item => item.type === 'photo' && !item.deleted && !item.toDelete
      ).length;

      const newVideoCount = updatedMedia.filter(
        item => item.type === 'video' && !item.deleted && !item.toDelete
      ).length;

      setPhotoCount(newPhotoCount);
      setVideoCount(newVideoCount);

      // Notificar al componente padre
      onChange(updatedMedia);
    } 
    // Si es un elemento existente, lo marcamos para eliminaci√≥n
    else {
      console.log("Marcando elemento existente para eliminaci√≥n:", itemToRemove);

      // Crear una copia para no mutar el estado directamente
      const updatedMedia = mediaItems.map((item, index) => {
        if (index === indexToRemove) {
          return {
            ...item,
            deleted: true, // Para la UI (ocultar)
            toDelete: true, // Para el backend (eliminar de la DB)
            isMain: false // Nunca mostrar como principal un elemento eliminado
          };
        }
        return item;
      });

      // Verificar si necesitamos asignar un nuevo elemento principal
      if (itemToRemove.isMain) {
        console.log("El elemento marcado para eliminaci√≥n era principal, buscando reemplazo");

        // Elementos no eliminados
        const visibleItems = updatedMedia.filter(
          item => !item.deleted && !item.toDelete
        );

        if (visibleItems.length > 0) {
          // Buscar primero una foto para establecer como principal
          const firstPhoto = visibleItems.find(item => item.type === 'photo');
          const firstValid = firstPhoto || visibleItems[0];

          if (firstValid) {
            console.log("Nuevo elemento principal despu√©s de eliminaci√≥n:", firstValid);

            // Crear array final con el nuevo elemento principal
            const finalMedia = updatedMedia.map(item => {
              if (item === firstValid) {
                return {
                  ...item,
                  isMain: true
                };
              }
              return item;
            });

            // Actualizar estado
            setMediaItems(finalMedia);

            // Notificar al componente padre
            onChange(finalMedia);

            // Actualizar contadores
            const newPhotoCount = finalMedia.filter(
              item => item.type === 'photo' && !item.deleted && !item.toDelete
            ).length;

            const newVideoCount = finalMedia.filter(
              item => item.type === 'video' && !item.deleted && !item.toDelete
            ).length;

            setPhotoCount(newPhotoCount);
            setVideoCount(newVideoCount);

            return;
          }
        }
      }

      // Actualizar estado
      setMediaItems(updatedMedia);

      // Notificar al componente padre
      onChange(updatedMedia);

      // Actualizar contadores
      const newPhotoCount = updatedMedia.filter(
        item => item.type === 'photo' && !item.deleted && !item.toDelete
      ).length;

      const newVideoCount = updatedMedia.filter(
        item => item.type === 'video' && !item.deleted && !item.toDelete
      ).length;

      setPhotoCount(newPhotoCount);
      setVideoCount(newVideoCount);

      // Buscar elementos marcados para eliminaci√≥n para debugging
      const itemsToDelete = updatedMedia.filter(item => item.toDelete === true);
      if (itemsToDelete.length > 0) {
        console.log("Elementos marcados para eliminaci√≥n:", 
          itemsToDelete.map(item => ({ url: item.url, type: item.type })));
      }

      // Mostrar mensaje de confirmaci√≥n
      toast({
        title: "Elemento marcado para eliminaci√≥n",
        description: "El cambio se aplicar√° al guardar el formulario",
      });
    }
  };

  // Establecer un medio como principal
  const handleSetMainMedia = (indexToSetMain: number) => {
    // CONTROL 1: Si est√° deshabilitado, no hacer nada
    if (disabled) return;

    console.log("===== MARCANDO COMO PRINCIPAL =====");
    console.log("√çndice seleccionado:", indexToSetMain);

    // CONTROL 2: Verificar que mediaItems es un array v√°lido
    if (!Array.isArray(mediaItems)) {
      console.error("Error cr√≠tico: mediaItems no es un array");
      toast({
        title: "Error interno",
        description: "Ocurri√≥ un problema con la gesti√≥n de medios",
        variant: "destructive"
      });
      return;
    }

    // CONTROL 3: Verificar que el √≠ndice es v√°lido
    if (indexToSetMain < 0 || indexToSetMain >= mediaItems.length) {
      console.error("√çndice para establecer como principal fuera de rango:", indexToSetMain);
      return;
    }

    try {
      // PASO 1: Crear una copia shallow de los elementos preservando IDs originales
      const clonedItems: MediaItem[] = mediaItems.map(item => {
        if (!item) {
          console.warn("Elemento null/undefined detectado en mediaItems");
          return null as unknown as MediaItem;
        }

        // Preservar el objeto completo sin crear nuevos IDs
        return {
          ...item,
          isMain: false // Por defecto, ninguno es principal (se establecer√° despu√©s)
        };
      }).filter(Boolean) as MediaItem[]; // Eliminar posibles null/undefined

      // PASO 2: Obtener una referencia segura al elemento a marcar como principal
      const itemToSetMain = clonedItems[indexToSetMain];

      // Verificaci√≥n adicional
      if (!itemToSetMain) {
        throw new Error("El elemento seleccionado no existe despu√©s de la clonaci√≥n");
      }

      // PASO 3: Verificar que el elemento cumple criterios para ser principal
      if (itemToSetMain.errorMessage) {
        console.warn("No se puede establecer como principal un elemento con errores");
        return;
      }

      if (itemToSetMain.deleted || itemToSetMain.toDelete) {
        console.warn("No se puede establecer como principal un elemento marcado para eliminaci√≥n");
        return;
      }

      // PASO 4: Marcar solo el elemento seleccionado como principal
      clonedItems.forEach((item, idx) => {
        item.isMain = (idx === indexToSetMain);
      });

      console.log("Estado actualizado - Elemento principal:", clonedItems[indexToSetMain].id);

      // Verificar elemento principal para debugging
      const mainItems = clonedItems.filter(item => item.isMain === true);
      console.log("¬øHay elemento principal expl√≠cito?", mainItems.length > 0);

      if (mainItems.length > 0) {
        console.log("ELEMENTO PRINCIPAL FINAL:", {
          tipo: mainItems[0].type || "desconocido",
          id: mainItems[0].id || "sin-id"
        });
      } else {
        console.error("¬°No se encontr√≥ un elemento principal despu√©s de la actualizaci√≥n!");
      }

      // PASO 5: Actualizar el estado y notificar al formulario padre de forma inmediata
      setMediaItems(clonedItems);

      // IMPORTANTE: Usar una copia fresca para evitar mutaciones accidentales
      onChange([...clonedItems]);

      // PASO 6: Aplicar la actualizaci√≥n de forma inmediata (env√≠o instant√°neo)
      // Construimos un objeto FormData y enviamos autom√°ticamente la actualizaci√≥n
      if (typeof window !== 'undefined' && itemToSetMain && itemToSetMain.url) {
        // Disparar un evento personalizado para notificar el cambio de elemento principal
        const customEvent = new CustomEvent('media-main-changed', {
          detail: {
            mediaItems: clonedItems,
            mainItem: itemToSetMain
          }
        });
        window.dispatchEvent(customEvent);

        console.log('Enviado evento de actualizaci√≥n principal:', customEvent);
      }

      // PASO 7: Notificaci√≥n visual al usuario
      const mainItem = clonedItems[indexToSetMain];
      const itemName = mainItem && mainItem.file ? mainItem.file.name : 
                     (mainItem && mainItem.url ? 'Archivo destacado' : 'Elemento');

      toast({
        title: "‚úì Elemento principal actualizado",
        description: `${itemName} ahora es la imagen destacada. Los cambios se guardan autom√°ticamente.`,
        duration: 2000
      });
      console.log("ELEMENTO PRINCIPAL FINAL:", {tipo: mainItems.length > 0 ? mainItems[0].type : 'ninguno'});
      console.log("=== FIN PROCESAMIENTO onChange ===");

    } catch (error) {
      // MANEJO DE ERRORES
      console.error("Error cr√≠tico al establecer elemento principal:", error);

      toast({
        title: "Error al establecer elemento principal",
        description: "Ha ocurrido un problema t√©cnico. Por favor, int√©ntalo de nuevo.",
        variant: "destructive",
        duration: 5000
      });
    }
  };

  // Renderizar miniatura para medio (foto o video)
  const renderMediaItem = (item: MediaItem, index: number) => {
    // Verificar si el item es v√°lido
    if (!item) {
      console.error(`Intentando renderizar un item undefined o null en el √≠ndice ${index}`);
      return null;
    }

    // No mostrar elementos eliminados
    if (item.deleted || item.toDelete) {
      return null;
    }

    return (
      <div 
        key={item.id || index}
        className={cn(
          "relative group border rounded-md overflow-hidden hover:shadow-md transition-all",
          "flex flex-col items-center justify-center",
          "min-w-[120px] w-[120px] h-[120px]",
          "bg-background",
          draggedItem === index ? "opacity-50 border-dashed" : "",
          dragOverItem === index ? "bg-accent" : "",
          item.errorMessage ? "border-destructive" : (item.isMain ? "border-primary border-2" : "border-border")
        )}
        draggable={!disabled}
        onDragStart={() => handleDragStart(index)}
        onDragOver={() => handleDragOver(index)}
        onDragEnd={handleDragEnd}
      >
        {/* Previsualizacion del medio */}
        <MediaPreview 
          file={item.file}
          url={item.url}
          previewUrl={item.previewUrl}
          type={item.type}
          className="w-full h-full object-cover"
        />

        {/* Acciones */}
        <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 bg-black/50 transition-opacity">
          {/* Bot√≥n para establecer como principal */}
          <Button
            type="button"
            onClick={() => handleSetMainMedia(index)}
            disabled={item.isMain || disabled}
            className={cn(
              "absolute top-1 left-1 h-7 w-7 p-0 rounded-full",
              item.isMain ? "bg-primary text-primary-foreground hover:bg-primary/90" : "bg-black/50 hover:bg-black/70"
            )}
            title="Establecer como principal"
          >
            {item.isMain ? <Star size={14} /> : <StarOff size={14} />}
          </Button>

          {/* Bot√≥n para eliminar */}
          <Button
            type="button"
            onClick={() => handleRemoveMedia(index)}
            disabled={disabled}
            className="absolute top-1 right-1 h-7 w-7 p-0 rounded-full bg-black/50 hover:bg-destructive/90"
            title="Eliminar"
          >
            <Trash size={14} />
          </Button>
        </div>

        {/* Icono de tipo */}
        <div className="absolute bottom-1 left-1 bg-black/50 text-white p-1 rounded-sm text-xs">
          {item.type === 'photo' ? <Image size={14} /> : <Video size={14} />}
        </div>

        {/* Indicador de elemento principal */}
        {item.isMain && (
          <Badge className="absolute bottom-1 right-1 text-xs bg-primary" variant="default">
            Principal
          </Badge>
        )}

        {/* Mensaje de error si lo hay */}
        {item.errorMessage && (
          <div className="absolute inset-0 flex items-center justify-center bg-black/70 text-white p-2 text-xs text-center">
            <div className="flex flex-col items-center space-y-1">
              <AlertCircle size={16} className="text-destructive" />
              <span>{item.errorMessage}</span>
            </div>
          </div>
        )}

        {/* Indicador de carga */}
        {item.uploading && (
          <div className="absolute inset-0 flex flex-col items-center justify-center bg-black/70 text-white">
            <Loader2 size={24} className="animate-spin mb-2" />
            <div className="text-xs">{item.uploadProgress || 0}%</div>
          </div>
        )}
      </div>
    );
  };

  // Conteo de elementos v√°lidos (no eliminados)
  const visibleItems = mediaItems.filter(item => !item.deleted && !item.toDelete);

  return (
    <div className="space-y-4">
      {/* Input oculto para seleccionar archivos */}
      <input 
        type="file" 
        ref={fileInputRef}
        className="hidden"
        onChange={handleFileChange}
        multiple
      />

      {/* √Årea de visualizaci√≥n de medios */}
      <div className="flex flex-wrap gap-3 min-h-[120px] border border-dashed rounded-md p-3">
        {visibleItems.length > 0 ? (
          visibleItems.map((item, index) => renderMediaItem(item, index))
        ) : (
          <div className="w-full h-full flex items-center justify-center text-muted-foreground text-sm py-8">
            No hay medios a√±adidos
          </div>
        )}
      </div>

      {/* Botones para agregar medios */}
      <div className="flex flex-wrap gap-2">
        <Button
          type="button"
          onClick={() => handleAddMedia(true)}
          disabled={disabled || photoCount >= maxPhotos}
          variant="outline"
          className="text-xs"
        >
          <Image className="mr-2" size={14} />
          A√±adir Foto ({photoCount}/{maxPhotos})
        </Button>

        <Button
          type="button"
          onClick={() => handleAddMedia(false)}
          disabled={disabled || videoCount >= maxVideos}
          variant="outline"
          className="text-xs"
        >
          <Video className="mr-2" size={14} />
          A√±adir Video ({videoCount}/{maxVideos})
        </Button>
      </div>
    </div>
  );
};

