import React, { useState, useEffect, useRef } from "react";
import SearchBar from "@/components/search/search-bar";
import GooglePlacesSearch from "@/components/search/google-places-search";
import EventFilters from "@/components/events/event-filters";
import { Circle, MapPin, MapIcon, PinIcon, Plus, Pin, MapPinned, Check, X, Compass, Filter } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import EventPin from "./event-pin";
// CategoryPin se define inline - no necesitamos un archivo separado
import mapboxgl, { LngLat } from 'mapbox-gl';
import { initializeMap, getUserLocation, defaultMapConfig, searchLocations, reverseGeocode } from "@/lib/mapbox";
// Para debugging - importar tambi√©n funciones de google
import { reverseGeocode as googleReverseGeocode, findNearbyPlaces } from "@/lib/google-maps";
import { createRoot } from 'react-dom/client';

// Import required types
type Event = {
  id: number;
  title: string;
  description: string;
  category: string;
  date: string;
  latitude: string | number;
  longitude: string | number;
  locationName: string;
  locationAddress: string;
  paymentType: string;
  price?: string | number;
  maxCapacity?: number;
  privacyType: string;
  organizerId: number;
  organizer: {
    id: number;
    name: string;
    avatar?: string;
  };
  attendees: Array<{
    id: number;
    user: {
      id: number;
      name: string;
      avatar?: string;
    };
  }>;
};

type MapViewProps = {
  onEventSelect: (event: Event) => void;
  onCreateEventClick: (locationData?: { 
    latitude: number, 
    longitude: number, 
    locationAddress: string, 
    locationName: string 
  }) => void;
  filters?: {
    category?: string[];
    paymentType?: string[];
    date?: string;
  };
  resetLocationOnFormClose?: boolean;
};



const MapView = ({ onEventSelect, onCreateEventClick, filters, resetLocationOnFormClose }: MapViewProps) => {
  const [filtersVisible, setFiltersVisible] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [locationMode, setLocationMode] = useState(false);
  const [eventsPanelVisible, setEventsPanelVisible] = useState(false);
  const [tempLocationMarker, setTempLocationMarker] = useState<mapboxgl.Marker | null>(null);
  const [tempLocationData, setTempLocationData] = useState<{
    latitude: number;
    longitude: number;
    locationAddress: string;
    locationName: string;
  } | null>(null);

  // Estado para mostrar botones de acci√≥n en ubicaci√≥n actual
  const [showActionsForLocation, setShowActionsForLocation] = useState(false);

  // Estado para guardar las coordenadas y datos de la ubicaci√≥n actual del mapa
  const [currentLocation, setCurrentLocation] = useState<{
    lng: number;
    lat: number;
    locationName?: string;
    locationAddress?: string;
  } | null>(null);

  // Estado para guardar eventos filtrados por una ubicaci√≥n espec√≠fica
  const [locationFilteredEvents, setLocationFilteredEvents] = useState<Event[]>([]);

  const { toast } = useToast();
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const mapRef = useRef<mapboxgl.Map | null>(null);
  const markersRef = useRef<mapboxgl.Marker[]>([]);
  const popupsRef = useRef<mapboxgl.Popup[]>([]);

  // Fetch events from API
  const { data: eventsData, isLoading, error } = useQuery({
    queryKey: ['/api/events'],
    refetchOnWindowFocus: true,
  });

  // Use fetched events or empty array if no data yet
  const events: Event[] = Array.isArray(eventsData) ? eventsData : [];

  // Filter events based on search and filters
  const filteredEvents = events.filter((event: Event) => {
    // Search filter
    if (searchTerm && !event.title.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }

    // Category filter
    if (filters?.category?.length && !filters.category.includes(event.category)) {
      return false;
    }

    // Payment type filter
    if (filters?.paymentType?.length && !filters.paymentType.includes(event.paymentType)) {
      return false;
    }

    // Date filter handling
    if (filters?.date && filters.date !== 'all') {
      const eventDate = new Date(event.date);
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const tomorrow = new Date(today);
      tomorrow.setDate(tomorrow.getDate() + 1);

      const nextWeekStart = new Date(today);
      nextWeekStart.setDate(nextWeekStart.getDate() + 7);

      const nextWeekEnd = new Date(nextWeekStart);
      nextWeekEnd.setDate(nextWeekEnd.getDate() + 7);

      // Get the current day of the week (0 = Sunday, 6 = Saturday)
      const currentDay = today.getDay();

      // Calculate days until next weekend (Friday and Saturday)
      const daysUntilFriday = (5 - currentDay + 7) % 7;
      const daysUntilSunday = (7 - currentDay) % 7;

      // Calculate next weekend dates
      const nextFriday = new Date(today);
      nextFriday.setDate(today.getDate() + daysUntilFriday);

      const nextSunday = new Date(today);
      nextSunday.setDate(today.getDate() + daysUntilSunday);

      switch (filters.date) {
        case 'today':
          // Event date must be today
          return eventDate.toDateString() === today.toDateString();

        case 'tomorrow':
          // Event date must be tomorrow
          return eventDate.toDateString() === tomorrow.toDateString();

        case 'this_week':
          // Event must be within the current week (next 7 days)
          return eventDate >= today && eventDate < nextWeekStart;

        case 'next_week':
          // Event must be within the next week (8-14 days from now)
          return eventDate >= nextWeekStart && eventDate < nextWeekEnd;

        case 'weekend':
          // Event must be on the upcoming weekend (Friday to Sunday)
          return eventDate >= nextFriday && eventDate <= nextSunday;

        default:
          return true;
      }
    }

    return true;
  });

  // Estado para controlar el men√∫ contextual
  const [contextMenu, setContextMenu] = useState<{
    visible: boolean;
    x: number;
    y: number;
    lngLat: mapboxgl.LngLat | null;
  }>({
    visible: false,
    x: 0,
    y: 0,
    lngLat: null
  });

  // Efecto principal para manejar los clics en el mapa (siempre activo)
  useEffect(() => {
    // Esta funci√≥n se ejecutar√° cada vez que el usuario haga clic en el mapa
    function handleMapClick(e: mapboxgl.MapMouseEvent) {
      console.log("Clic en el mapa detectado", locationMode ? "en modo ubicaci√≥n" : "en modo normal");

      // Detener la propagaci√≥n para evitar que otros handlers cierren nuestro men√∫
      e.originalEvent.stopPropagation();

      // Limpiar cualquier marcador temporal previo
      if (tempLocationMarker) {
        tempLocationMarker.remove();
        setTempLocationMarker(null);
      }

      // SIEMPRE mostrar el men√∫ contextual sin importar el modo
      console.log("Abriendo men√∫ contextual en", e.lngLat);

      // IMPORTANTE: Activar el modo de ubicaci√≥n al hacer clic en el mapa
      // Esto permite que el men√∫ contextual se muestre sin tener que hacer clic en "Crear un evento" primero
      if (!locationMode) {
        console.log("Activando modo de ubicaci√≥n autom√°ticamente");
        setLocationMode(true);
      }

      // Crear un nuevo marcador en la ubicaci√≥n del clic
      if (mapRef.current) {
        // Crear marcador con forma de gota estilo Google Maps pero amarillo
        const markerElement = document.createElement('div');
        markerElement.innerHTML = `
          <svg width="25" height="41" viewBox="0 0 25 41" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M12.5 0C5.596 0 0 5.596 0 12.5C0 21.563 12.5 41 12.5 41S25 21.563 25 12.5C25 5.596 19.404 0 12.5 0Z" fill="#FFEB3B"/>
            <path d="M12.5 0C5.596 0 0 5.596 0 12.5C0 21.563 12.5 41 12.5 41S25 21.563 25 12.5C25 5.596 19.404 0 12.5 0Z" stroke="#F57C00" stroke-width="1"/>
            <circle cx="12.5" cy="12.5" r="4" fill="white"/>
          </svg>
        `;
        markerElement.style.width = '25px';
        markerElement.style.height = '41px';
        markerElement.style.position = 'relative';
        markerElement.style.zIndex = '5';
        markerElement.style.cursor = 'pointer';

        // Crear el marcador
        const marker = new mapboxgl.Marker({
          element: markerElement,
          draggable: false
        })
          .setLngLat(e.lngLat)
          .addTo(mapRef.current);

        // Guardar referencia del marcador
        setTempLocationMarker(marker);

        // PASO CRUCIAL: Mostrar el men√∫ contextual INMEDIATAMENTE
        const point = mapRef.current.project(e.lngLat);

        // Actualizar en un solo paso para evitar renders parciales que causen problemas
        setContextMenu({
          visible: true,
          x: point.x,
          y: point.y - 20, // Posicionar por encima del marcador
          lngLat: e.lngLat
        });

        // Tambi√©n guardar los datos de ubicaci√≥n temporales
        setTempLocationData({
          latitude: e.lngLat.lat,
          longitude: e.lngLat.lng,
          locationName: "Ubicaci√≥n seleccionada", 
          locationAddress: "Cargando direcci√≥n..."
        });
      }

      // Usar ambos servicios en paralelo para obtener resultados m√°s completos
