          <button 
            className="p-2 text-left hover:bg-gray-100 text-sm flex items-center gap-2"
            onClick={() => {
              closeContextMenu(); // Primero cerramos el men√∫
              // Luego buscamos eventos cerca de la ubicaci√≥n seleccionada
