            </button>

            {/* Confirm Location Button */}
            <button
              className="flex-1 py-3 bg-primary rounded-xl shadow-lg text-white flex items-center justify-center gap-2 font-medium"
              onClick={confirmLocationSelection}
              disabled={!tempLocationData}
            >
              <Check size={20} />
